'use client';

import { useState, useEffect } from 'react';
import { NextPage } from 'next';
import { listBookings, updateBooking, deleteBooking } from '@/lib/services/booking-service';
import { Booking } from '@/types/booking';
import {
  AdminPageWrapper,
  GridSection,
  StatCard,
  InfoCard,
  DataTable,
  DataTableColumn,
  DataTableAction,
  ToastProvider,
  useToast,
  Span
} from '@/components/ui';

function AdminBookingsPageContent() {
  const { addToast } = useToast();
  const [bookings, setBookings] = useState<Booking[]>([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState<string | null>(null);

  useEffect(() => {
    fetchBookings();
  }, []);

  const fetchBookings = async () => {
    try {
      setError(null);
      setLoading(true);
      console.log('üîÑ Fetching bookings...');

      const fetchedBookings = await listBookings();
      console.log('‚úÖ Bookings fetched:', fetchedBookings.length, fetchedBookings);

      // If no real bookings exist, use mock data for demonstration
      if (fetchedBookings.length === 0) {
        console.log('üìù No bookings found, using mock data for demonstration');
        const mockBookings: Booking[] = [
          {
            id: 'mock-1',
            name: 'John Smith',
            email: 'john@example.com',
            phone: '203-555-0123',
            pickupLocation: 'Fairfield Station',
            dropoffLocation: 'JFK Airport',
            pickupDateTime: new Date('2024-12-25T10:00:00Z'),
            passengers: 2,
            fare: 150,
            status: 'confirmed',
            depositPaid: true,
            balanceDue: 50,
            tipAmount: 25,
            cancellationFee: 0,
            createdAt: new Date(),
            updatedAt: new Date()
          },
          {
            id: 'mock-2', 
            name: 'Sarah Johnson',
            email: 'sarah@example.com',
            phone: '203-555-0124',
            pickupLocation: 'Stamford Downtown',
            dropoffLocation: 'LaGuardia Airport',
            pickupDateTime: new Date('2024-12-26T14:30:00Z'),
            passengers: 1,
            fare: 120,
            status: 'pending',
            depositPaid: false,
            balanceDue: 120,
            tipAmount: 0,
            cancellationFee: 0,
            createdAt: new Date(),
            updatedAt: new Date()
          },
          {
            id: 'mock-3',
            name: 'Mike Davis',
            email: 'mike@example.com', 
            phone: '203-555-0125',
            pickupLocation: 'Greenwich Harbor',
            dropoffLocation: 'Newark Airport',
            pickupDateTime: new Date('2024-12-24T08:15:00Z'),
            passengers: 3,
            fare: 180,
            status: 'completed',
            depositPaid: true,
            balanceDue: 0,
            tipAmount: 30,
            cancellationFee: 0,
            createdAt: new Date(),
            updatedAt: new Date()
          },
          {
            id: 'mock-4',
            name: 'Emily Chen',
            email: 'emily@example.com',
            phone: '203-555-0126',
            pickupLocation: 'Westport Downtown',
            dropoffLocation: 'Bradley Airport',
            pickupDateTime: new Date('2024-12-27T12:00:00Z'),
            passengers: 1,
            fare: 95,
            status: 'cancelled',
            depositPaid: false,
            balanceDue: 0,
            tipAmount: 0,
            cancellationFee: 25,
            createdAt: new Date(),
            updatedAt: new Date()
          }
        ];

        setBookings(mockBookings);
      } else {
        setBookings(fetchedBookings);
      }
    } catch (err) {
      console.error('‚ùå Error fetching bookings:', err);
      setError('Failed to load bookings. Please try again.');
      addToast('error', 'Failed to load bookings');
    } finally {
      setLoading(false);
    }
  };

  const handleStatusUpdate = async (booking: Booking, newStatus: Booking['status']) => {
    try {
      if (!booking.id) {
        addToast('error', 'Cannot update booking: missing ID');
        return;
      }

      await updateBooking(booking.id, { status: newStatus });

      // Update local state
      setBookings(prev => prev.map(b => 
        b.id === booking.id ? { ...b, status: newStatus } : b
      ));

      addToast('success', `Booking ${newStatus}`);
    } catch (err) {
      console.error('Error updating booking status:', err);
      addToast('error', 'Failed to update booking status');
    }
  };

  const handleDeleteBooking = async (booking: Booking) => {
    if (!booking.id) {
      addToast('error', 'Cannot delete booking: missing ID');
      return;
    }

    if (!confirm(`Are you sure you want to delete the booking for ${booking.name}?`)) {
      return;
    }

    try {
      await deleteBooking(booking.id);

      // Update local state
      setBookings(prev => prev.filter(b => b.id !== booking.id));

      addToast('success', 'Booking deleted');
    } catch (err) {
      console.error('Error deleting booking:', err);
      addToast('error', 'Failed to delete booking');
    }
  };

  // Calculate stats
  const totalBookings = bookings.length;
  const pendingBookings = bookings.filter(b => b.status === 'pending').length;
  const confirmedBookings = bookings.filter(b => b.status === 'confirmed').length;
  const completedBookings = bookings.filter(b => b.status === 'completed').length;
  const totalRevenue = bookings
    .filter(b => b.status === 'completed')
    .reduce((sum, b) => sum + b.fare, 0);
  const totalTips = bookings
    .filter(b => b.status === 'completed')
    .reduce((sum, b) => sum + (b.tipAmount || 0), 0);
  const totalCancFees = bookings
    .filter(b => b.status === 'cancelled')
    .reduce((sum, b) => sum + (b.cancellationFee || 0), 0);

  // Header actions
  const headerActions = [
    {
      label: 'Refresh',
      icon: 'üîÑ',
      onClick: fetchBookings,
      variant: 'outline' as const
    },
    {
      label: 'Export',
      icon: 'üìä',
      onClick: () => addToast('info', 'Export feature coming soon'),
      variant: 'outline' as const
    }
  ];

  // Status renderer
  const renderStatus = (status: string) => {
    const getStatusClass = (status: string) => {
      switch (status.toLowerCase()) {
        case 'confirmed':
          return 'status-badge-confirmed';
        case 'pending':
          return 'status-badge-pending';
        case 'completed':
          return 'status-badge-completed';
        case 'cancelled':
          return 'status-badge-cancelled';
        default:
          return 'status-badge-default';
      }
    };

    return (
      <Span className={`status-badge ${getStatusClass(status)}`}>
        {status.charAt(0).toUpperCase() + status.slice(1)}
      </Span>
    );
  };

  // Table columns configuration
  const columns: DataTableColumn<Booking>[] = [
    {
      key: 'name',
      label: 'Customer',
      sortable: true,
      render: (_, booking) => (
        <div >
          <div >{booking.name}</div>
          <div >{booking.email}</div>
        </div>
      )
    },
    {
      key: 'pickupLocation',
      label: 'Route',
      sortable: true,
      render: (_, booking) => (
        <div >
          <div >{booking.pickupLocation}</div>
          <div >‚Üí {booking.dropoffLocation}</div>
        </div>
      )
    },
    {
      key: 'pickupDateTime',
      label: 'Pickup Time',
      sortable: true,
      render: (value) => {
        const date = new Date(value);
        return (
          <div >
            <div >{date.toLocaleDateString()}</div>
            <div >{date.toLocaleTimeString()}</div>
          </div>
        );
      }
    },
    {
      key: 'fare',
      label: 'Fare',
      sortable: true,
      render: (value) => (
        <Span >${(value || 0).toFixed(2)}</Span>
      )
    },
    {
      key: 'status',
      label: 'Status',
      sortable: true,
      render: (value) => renderStatus(value)
    }
  ];

  // Table actions
  const actions: DataTableAction<Booking>[] = [
    {
      label: 'View',
      icon: 'üëÅÔ∏è',
      onClick: (booking) => window.open(`/booking/${booking.id}`, '_blank'),
      variant: 'outline'
    },
    {
      label: 'Confirm',
      icon: '‚úÖ',
      onClick: (booking) => handleStatusUpdate(booking, 'confirmed'),
      variant: 'primary',
      condition: (booking) => booking.status === 'pending'
    },
    {
      label: 'Complete',
      icon: 'üèÅ',
      onClick: (booking) => handleStatusUpdate(booking, 'completed'),
      variant: 'primary', 
      condition: (booking) => booking.status === 'confirmed'
    },
    {
      label: 'Cancel',
      icon: '‚ùå',
      onClick: (booking) => handleStatusUpdate(booking, 'cancelled'),
      variant: 'outline',
      condition: (booking) => booking.status !== 'completed' && booking.status !== 'cancelled'
    },
    {
      label: 'Delete',
      icon: 'üóëÔ∏è',
      onClick: handleDeleteBooking,
      variant: 'outline'
    }
  ];

  return (
    <AdminPageWrapper
      title="Booking Dashboard"
      subtitle="Manage customer bookings and reservations"
      actions={headerActions}
      loading={loading}
      error={error}
      loadingMessage="Loading bookings..."
      errorTitle="Booking Load Error"
    >
      {/* Stats Overview */}
      <GridSection variant="stats" columns={4}>
        <StatCard
          title="Total Bookings"
          icon="üìä"
          statNumber={totalBookings.toString()}
          statChange={`${pendingBookings} pending, ${confirmedBookings} confirmed`}
          changeType="neutral"
        />
        <StatCard
          title="Total Revenue"
          icon="üí∞"
          statNumber={`$${totalRevenue.toFixed(2)}`}
          statChange={`${completedBookings} completed bookings`}
          changeType="positive"
        />
        <StatCard
          title="Tips Collected"
          icon="üíµ"
          statNumber={`$${totalTips.toFixed(2)}`}
          statChange="From completed rides"
          changeType="positive"
        />
        <StatCard
          title="Cancellation Fees"
          icon="‚ö†Ô∏è"
          statNumber={`$${totalCancFees.toFixed(2)}`}
          statChange="From cancelled bookings"
          changeType="neutral"
        />
      </GridSection>

      {/* Bookings Table */}
      <GridSection variant="content" columns={1}>
        <InfoCard
          title="üìÖ All Bookings"
          description="Search, sort, and manage customer bookings"
        >
          <DataTable
            data={bookings}
            columns={columns}
            actions={actions}
            loading={loading}
            searchPlaceholder="Search by customer, location, or status..."
            emptyMessage="No bookings found. Create your first booking to get started."
            emptyIcon="üìÖ"
            pageSize={10}
            rowClassName={(booking) => booking.status === 'cancelled' ? 'opacity-60' : ''}
            onRowClick={(booking) => console.log('Clicked booking:', booking.id)}
          />
        </InfoCard>
      </GridSection>
    </AdminPageWrapper>
  );
}

const AdminBookingsPage: NextPage = () => {
  return (
    <ToastProvider>
      <AdminBookingsPageContent />
    </ToastProvider>
  );
};

export default AdminBookingsPage;

'use client';

import { useEffect, useState } from 'react';
import FullCalendar from '@fullcalendar/react';
import dayGridPlugin from '@fullcalendar/daygrid';
import { listBookings } from '../../../lib/services/booking-service';
import { 
  AdminPageWrapper, 
  InfoCard, 
  GridSection,
  Container,
  H3,
  Text,
  Span
} from '@/components/ui';

const CalendarPage = () => {
  const [events, setEvents] = useState<any[]>([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState<string | null>(null);

  useEffect(() => {
    fetchCalendarData();
  }, []);

  const fetchCalendarData = async () => {
    try {
      setError(null);
      setLoading(true);
      console.log('üìÖ Loading calendar data...');

      const bookings = await listBookings();
      const calendarEvents = bookings.map((booking) => ({
        id: booking.id,
        title: `${booking.name} ‚Äì $${booking.fare}`,
        start: booking.pickupDateTime,
        url: `/booking/${booking.id}`,
        backgroundColor: getEventColor(booking.status),
        borderColor: getEventColor(booking.status),
        textColor: '#ffffff'
      }));

      console.log('‚úÖ Calendar events loaded:', calendarEvents.length);
      setEvents(calendarEvents);
    } catch (err) {
      console.error('‚ùå Failed to load calendar data:', err);
      setError('Failed to load calendar data. Please try again.');
    } finally {
      setLoading(false);
    }
  };

  const getEventColor = (status: string) => {
    switch (status) {
      case 'cancelled': return '#dc2626';
      case 'confirmed': return '#059669';
      case 'completed': return '#0d9488';
      case 'pending': return '#d97706';
      default: return '#6b7280';
    }
  };

  const headerActions = [
    { 
      label: 'Refresh',
      onClick: fetchCalendarData,
      variant: 'outline' as const,
      disabled: loading
    },
    { 
      label: 'View Bookings', 
      href: '/admin/bookings', 
      variant: 'outline' as const 
    },
    { 
      label: 'Add Booking', 
      href: '/admin/bookings/new', 
      variant: 'primary' as const 
    }
  ];

  return (
    <AdminPageWrapper
      title="Ride Calendar"
      subtitle="View all bookings in calendar format"
      actions={headerActions}
      loading={loading}
      error={error}
      loadingMessage="Loading calendar data..."
      errorTitle="Calendar Load Error"
    >
      <GridSection variant="content" columns={1}>
        <InfoCard
          title="üìÖ Monthly Booking Calendar"
          description={`Showing ${events.length} bookings with color-coded status`}
        >
          {!loading && !error && (
            <Container>
              {typeof window !== 'undefined' && (
                <FullCalendar
                  plugins={[dayGridPlugin]}
                  initialView="dayGridMonth"
                  events={events}
                  height="auto"
                  headerToolbar={{
                    left: 'prev,next today',
                    center: 'title',
                    right: 'dayGridMonth,dayGridWeek'
                  }}
                  eventClick={(info: any) => {
                    if (info.event.url) {
                      window.open(info.event.url, '_blank');
                      info.jsEvent.preventDefault();
                    }
                  }}
                  eventDisplay="block"
                  dayMaxEvents={3}
                  moreLinkClick="popover"
                />
              )}
            </Container>
          )}

          {!loading && !error && events.length === 0 && (
            <Container>
              <Container>üìÖ</Container>
              <H3>No bookings found</H3>
              <Text>No bookings scheduled for this month.</Text>
            </Container>
          )}
        </InfoCard>
      </GridSection>

      {/* Calendar Legend */}
      <GridSection variant="content" columns={1}>
        <InfoCard
          title="üìä Status Legend"
          description="Color coding for booking statuses"
        >
          <Container>
            <Container >
              <Span>üü°</Span>
              <Span >Pending</Span>
            </Container>
            <Container >
              <Span>üü¢</Span>
              <Span >Confirmed</Span>
            </Container>
            <Container >
              <Span>üîµ</Span>
              <Span >Completed</Span>
            </Container>
            <Container >
              <Span>üî¥</Span>
              <Span >Cancelled</Span>
            </Container>
          </Container>
        </InfoCard>
      </GridSection>
    </AdminPageWrapper>
  );
};

export default CalendarPage;

'use client';

import { useState, useEffect, useMemo } from 'react';
import withAuth from '../../withAuth';
import { cmsService } from '@/lib/services/cms-service';
import { BusinessSettings } from '@/types/cms';
import { 
  AdminPageWrapper,
  SettingSection,
  SettingInput,
  StatusMessage,
  ToastProvider,
  useToast,
  GridSection,
  Container,
  Text,
  H3,
  Span
} from '@/components/ui';
import { Stack } from '@/components/ui/containers';

function BusinessPageContent() {
  const { addToast } = useToast();
  const [settings, setSettings] = useState<BusinessSettings | null>(null);
  const [loading, setLoading] = useState(true);
  const [saving, setSaving] = useState(false);
  const [error, setError] = useState<string | null>(null);

  useEffect(() => {
    loadBusinessSettings();
  }, []);

  const loadBusinessSettings = async () => {
    setLoading(true);
    setError(null);

    try {
      const businessSettings = await cmsService.getBusinessSettings();
      setSettings(businessSettings);
      addToast('success', 'Business settings loaded successfully');
    } catch (error) {
      console.error('Error loading business settings:', error);
      const errorMsg = 'Failed to load business settings';
      setError(errorMsg);
      addToast('error', errorMsg);
    } finally {
      setLoading(false);
    }
  };

  const handleSave = async () => {
    if (!settings) return;

    setSaving(true);
    setError(null);

    try {
      await cmsService.updateBusinessSettings(settings);
      addToast('success', 'Business settings saved successfully!');
    } catch (error) {
      console.error('Error saving business settings:', error);
      const errorMsg = 'Failed to save business settings';
      setError(errorMsg);
      addToast('error', errorMsg);
    } finally {
      setSaving(false);
    }
  };

  const handleInputChange = (section: keyof BusinessSettings, field: string, value: string) => {
    if (!settings) return;

    setSettings({
      ...settings,
      [section]: {
        ...settings[section],
        [field]: value
      }
    });
  };

  const handleSocialChange = (platform: string, value: string) => {
    if (!settings) return;

    setSettings({
      ...settings,
      social: {
        ...settings.social,
        [platform]: value
      }
    });
  };

  // Header actions
  const headerActions = useMemo(() => [
    { 
      label: 'Back to CMS',
      onClick: (): void => { window.location.href = '/admin/cms'; },
      variant: 'outline' as const,
      icon: 'üîô'
    },
    { 
      label: 'Reload Settings',
      onClick: loadBusinessSettings,
      variant: 'outline' as const,
      disabled: loading,
      icon: 'üîÑ'
    },
    { 
      label: 'Save Changes',
      onClick: handleSave,
      variant: 'primary' as const,
      disabled: !settings || saving,
      icon: 'üíæ'
    }
  ], [settings, loading, saving]);

  if (loading) {
    return (
      <AdminPageWrapper
        title="Business Settings"
        subtitle="Configure your company information and branding"
        loading={true}
        loadingMessage="Loading business settings..."
      >
        <div />
      </AdminPageWrapper>
    );
  }

  if (error) {
    return (
      <AdminPageWrapper
        title="Business Settings"
        subtitle="Configure your company information and branding"
        error={error}
        errorTitle="Settings Load Error"
      >
        <div />
      </AdminPageWrapper>
    );
  }

  if (!settings) {
    return (
      <AdminPageWrapper
        title="Business Settings"
        subtitle="Configure your company information and branding"
        error="No business settings found"
        errorTitle="Settings Not Found"
      >
        <div />
      </AdminPageWrapper>
    );
  }

  return (
    <AdminPageWrapper
      title="Business Settings"
      subtitle="Configure your company information and branding"
      actions={headerActions}
      loading={saving}
      loadingMessage="Saving business settings..."
    >
      {saving && (
        <StatusMessage
          type="info"
          message="Please wait while we save your business settings..."
        />
      )}

      {settings && (
        <GridSection variant="content" columns={1}>
          <Container>
            {/* Company Information */}
            <SettingSection
              title="Company Information"
              description="Basic company details and contact information"
              icon="üè¢"
            >
              <Stack spacing="md">
                <SettingInput
                  id="company-name"
                  label="Company Name"
                  description="Your official business name"
                  value={settings.company.name}
                  onChange={(value) => handleInputChange('company', 'name', value)}
                  placeholder="Your Company Name"
                  icon="üè¢"
                />

                <SettingInput
                  id="company-tagline"
                  label="Tagline"
                  description="A brief description of your business"
                  value={settings.company.tagline || ''}
                  onChange={(value) => handleInputChange('company', 'tagline', value)}
                  placeholder="Your company tagline"
                  icon="üí¨"
                />

                <SettingInput
                  id="company-phone"
                  label="Phone Number"
                  description="Primary contact phone number"
                  value={settings.company.phone}
                  onChange={(value) => handleInputChange('company', 'phone', value)}
                  placeholder="(555) 123-4567"
                  icon="üìû"
                />

                <SettingInput
                  id="company-email"
                  label="Email Address"
                  description="Primary contact email address"
                  type="email"
                  value={settings.company.email}
                  onChange={(value) => handleInputChange('company', 'email', value)}
                  placeholder="contact@company.com"
                  icon="‚úâÔ∏è"
                />

                <SettingInput
                  id="company-address"
                  label="Address"
                  description="Business physical address"
                  value={settings.company.address || ''}
                  onChange={(value) => handleInputChange('company', 'address', value)}
                  placeholder="123 Main St, City, State 12345"
                  icon="üìç"
                />

                <SettingInput
                  id="company-hours"
                  label="Business Hours"
                  description="Operating hours for customers"
                  value={settings.company.hours || ''}
                  onChange={(value) => handleInputChange('company', 'hours', value)}
                  placeholder="Mon-Fri 9am-5pm"
                  icon="üïí"
                />
              </Stack>
            </SettingSection>

            {/* Social Media */}
            <SettingSection
              title="Social Media"
              description="Links to your social media profiles"
              icon="üì±"
            >
              <Stack spacing="md">
                <SettingInput
                  id="social-facebook"
                  label="Facebook URL"
                  description="Link to your Facebook business page"
                  value={settings.social.facebook || ''}
                  onChange={(value) => handleSocialChange('facebook', value)}
                  placeholder="https://facebook.com/yourpage"
                  icon="üìò"
                />

                <SettingInput
                  id="social-instagram"
                  label="Instagram URL"
                  description="Link to your Instagram business account"
                  value={settings.social.instagram || ''}
                  onChange={(value) => handleSocialChange('instagram', value)}
                  placeholder="https://instagram.com/yourpage"
                  icon="üì∑"
                />

                <SettingInput
                  id="social-twitter"
                  label="Twitter URL"
                  description="Link to your Twitter business account"
                  value={settings.social.twitter || ''}
                  onChange={(value) => handleSocialChange('twitter', value)}
                  placeholder="https://twitter.com/yourpage"
                  icon="üê¶"
                />
              </Stack>
            </SettingSection>

            {/* Branding */}
            <SettingSection
              title="Branding"
              description="Visual identity and brand colors"
              icon="üé®"
            >
              <Stack spacing="md">
                <Container>
                  <SettingInput
                    id="brand-primary-color"
                    label="Primary Color"
                    description="Main brand color for buttons and accents"
                    value={settings.branding.primaryColor}
                    onChange={(value) => handleInputChange('branding', 'primaryColor', value)}
                    placeholder="#1f2937"
                    icon="üé®"
                  />
                  <Container>
                    <Span>Preview:</Span>
                    <Container>
                      <span>Color preview</span>
                    </Container>
                  </Container>
                </Container>

                <Container>
                  <SettingInput
                    id="brand-secondary-color"
                    label="Secondary Color"
                    description="Secondary brand color for highlights"
                    value={settings.branding.secondaryColor}
                    onChange={(value) => handleInputChange('branding', 'secondaryColor', value)}
                    placeholder="#3b82f6"
                    icon="üé®"
                  />
                  <Container>
                    <Span>Preview:</Span>
                    <Container>
                      <span>Color preview</span>
                    </Container>
                  </Container>
                </Container>

                <SettingInput
                  id="brand-logo-url"
                  label="Logo URL"
                  description="Direct link to your company logo image"
                  value={settings.branding.logoUrl || ''}
                  onChange={(value) => handleInputChange('branding', 'logoUrl', value)}
                  placeholder="https://example.com/logo.png"
                  icon="üñºÔ∏è"
                />
              </Stack>
            </SettingSection>

            {/* Business Preview */}
            <SettingSection
              title="Business Card Preview"
              description="How your business information will appear to customers"
              icon="üëÄ"
            >
              <Container>
                <Container>
                  <Container>
                    <H3>{settings.company.name}</H3>
