'use client';

import { useState, useEffect } from 'react';
import { useSearchParams } from 'next/navigation';
import { UnifiedLayout } from '@/components/layout';
import { 
  GridSection,
  InfoCard,
  ActionButtonGroup,
  LoadingSpinner,
  Text
} from '@/components/ui';
import { Booking } from '@/types/booking';

function SuccessPageContent() {
  const searchParams = useSearchParams();
  const bookingId = searchParams.get('bookingId');
  const [booking, setBooking] = useState<Booking | null>(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState<string | null>(null);

  const fetchBookingDetails = async () => {
    if (!bookingId) {
      setError('No booking ID provided');
      setLoading(false);
      return;
    }

    try {
      const response = await fetch(`/api/booking/${bookingId}`);
      if (!response.ok) {
        throw new Error('Failed to fetch booking details');
      }
      const data = await response.json();
      setBooking(data);
    } catch (err) {
      setError(err instanceof Error ? err.message : 'Failed to load booking');
    } finally {
      setLoading(false);
    }
  };

  useEffect(() => {
    fetchBookingDetails();
  }, [bookingId]);

  const successActions = [
    {
      label: 'Book Another Ride',
      onClick: () => window.location.href = '/book',
      variant: 'primary' as const,
      icon: 'üöó'
    },
    {
      label: 'View My Booking',
      onClick: () => window.location.href = `/booking/${bookingId}`,
      variant: 'secondary' as const,
      icon: 'üìã'
    },
    {
      label: 'Contact Support',
      onClick: () => window.location.href = '/help',
      variant: 'outline' as const,
      icon: 'üí¨'
    }
  ];

  if (loading) {
    return (
      <UnifiedLayout 
        layoutType="status"
        title="Loading..."
        subtitle="Please wait while we load your booking details"
      >
        <GridSection variant="content" columns={1}>
          <InfoCard
            title="Loading..."
            description="Loading your booking details..."
          >
            <LoadingSpinner size="lg" />
          </InfoCard>
        </GridSection>
      </UnifiedLayout>
    );
  }

  return (
    <UnifiedLayout 
      layoutType="status"
      title="üéâ Booking Confirmed!"
      subtitle={booking?.depositPaid ? "Payment successful - You're all set!" : "Your booking is confirmed"}
    >
      {error && (
        <GridSection variant="content" columns={1}>
          <InfoCard
            title="‚ö†Ô∏è Error Loading Booking"
            description={error}
          >
            <Text>Please try refreshing the page or contact support if the problem persists.</Text>
          </InfoCard>
        </GridSection>
      )}

      {/* Success Message */}
      <GridSection variant="content" columns={1}>
        <InfoCard
          title={booking?.depositPaid ? "‚úÖ Payment Successful!" : "üìù Booking Created!"}
          description={booking?.depositPaid 
            ? "Your deposit has been processed and your ride is confirmed"
            : "Your booking has been created. Payment can be completed before your ride"
          }
        >
          <ActionButtonGroup buttons={successActions} />
        </InfoCard>
      </GridSection>

      {/* Booking Details */}
      {booking && (
        <GridSection variant="content" columns={2}>
          <InfoCard
            title="üöó Trip Details"
            description="Your journey information"
          >
            <Text><strong>From:</strong> {booking.pickupLocation}</Text>
            <Text><strong>To:</strong> {booking.dropoffLocation}</Text>
            <Text><strong>When:</strong> {new Date(booking.pickupDateTime).toLocaleString()}</Text>
            <Text><strong>Passengers:</strong> {booking.passengers}</Text>
          </InfoCard>

          <InfoCard
            title="üí∞ Payment Status"
            description="Your payment information"
          >
            <Text><strong>Total Fare:</strong> ${booking.fare}</Text>
            <Text><strong>Deposit:</strong> ${booking.depositAmount} {booking.depositPaid ? '‚úÖ Paid' : '‚è≥ Pending'}</Text>
            <Text><strong>Balance Due:</strong> ${booking.balanceDue || 0}</Text>
          </InfoCard>
        </GridSection>
      )}

      {/* Next Steps */}
      <GridSection variant="content" columns={1}>
        <InfoCard
          title="üìã What Happens Next?"
          description="Here's what you can expect from us"
        >
          <ul>
            <li>üìß You&apos;ll receive a confirmation email with all booking details</li>
            <li>üì± We&apos;ll send you SMS updates about your driver and pickup time</li>
            <li>üë®‚Äçüíº Your driver will contact you 30 minutes before pickup</li>
            <li>‚úàÔ∏è We monitor your flight for any delays or changes</li>
          </ul>

          <ActionButtonGroup buttons={successActions} />
        </InfoCard>
      </GridSection>

      {/* Emergency Contact */}
      <GridSection variant="content" columns={1}>
        <InfoCard
          title="üÜò Need Help?"
          description="Contact us anytime if you have questions or need to make changes"
        >
          <Text>üìû (203) 555-0123</Text>
          <Text>Save this number! Our drivers are available to assist you.</Text>
        </InfoCard>
      </GridSection>
    </UnifiedLayout>
  );
}

export default SuccessPageContent;

'use client';

import React from 'react';
import { Input, Label } from '@/components/ui';
import { Textarea } from '@/components/ui';
import { useEditMode } from './EditModeProvider';

interface EditableFieldProps {
  value: string;
  onChange: (value: string) => void;
  label?: string;
  placeholder?: string;
  className?: string;
  type?: 'input' | 'textarea';
  rows?: number;
  size?: 'sm' | 'md' | 'lg' | 'xl';
}

export const EditableField: React.FC<EditableFieldProps> = ({
  value,
  onChange,
  label,
  placeholder,
  className = '',
  type = 'input',
  rows = 3,
  size = 'md'
}) => {
  const { editMode } = useEditMode();

  const sizeClasses = {
    sm: 'text-sm',
    md: 'text-base',
    lg: 'text-lg',
    xl: 'text-xl'
  };

  const inputSizeClasses = {
    sm: 'h-8 px-3',
    md: 'h-10 px-4',
    lg: 'h-12 px-4',
    xl: 'h-14 px-4'
  };

  if (editMode) {
    return (
      <div>
        {label && (
          <Label>
            {label}
          </Label>
        )}
        {type === 'textarea' ? (
          <Textarea
            value={value}
            onChange={(e: React.ChangeEvent<HTMLTextAreaElement>) => onChange(e.target.value)}
            placeholder={placeholder}
            className={className}
            rows={rows}
          />
        ) : (
          <Input
            value={value}
            onChange={(e: React.ChangeEvent<HTMLInputElement>) => onChange(e.target.value)}
            placeholder={placeholder}
            className={className}
          />
        )}
      </div>
    );
  }

  // Display mode
  if (type === 'textarea') {
    // Check if the content contains HTML tags
    const containsHTML = /<[^>]*>/.test(value);

    if (containsHTML) {
      return (
        <div 
          className={className}
          dangerouslySetInnerHTML={{ __html: value || placeholder || '' }}
        />
      );
    }

    return (
      <div className={className}>
        {value || placeholder}
      </div>
    );
  }

  return (
    <div className={className}>
      {value || placeholder}
    </div>
  );
};

// Specialized components for common use cases
export const EditableTitle: React.FC<Omit<EditableFieldProps, 'type' | 'size'>> = (props) => (
  <EditableField {...props} type="input" size="xl" />
);

export const EditableSubtitle: React.FC<Omit<EditableFieldProps, 'type' | 'size'>> = (props) => (
  <EditableField {...props} type="input" size="lg" />
);

export const EditableContent: React.FC<Omit<EditableFieldProps, 'type' | 'size'> & { rows?: number }> = (props) => (
  <EditableField {...props} type="textarea" size="md" />
);

export const EditableLabel: React.FC<Omit<EditableFieldProps, 'type' | 'size'>> = (props) => (
  <EditableField {...props} type="input" size="md" />
);

'use client';

import { useState, useRef, useEffect } from 'react';
import { Button } from '@/components/ui/button';
import { Card, CardBody, CardHeader, CardTitle } from '@/components/ui/card';
import { Textarea } from '@/components/ui';
import { Select, SelectContent, SelectItem, SelectTrigger, SelectValue } from '@/components/ui/select';
import { Badge } from '@/components/ui';
import { Container, Text, H3, H4 } from '@/components/ui';
import { feedbackService, type PageComment } from '@/lib/services/feedback-service';
import { MessageSquare, X, CheckCircle, AlertCircle, Star } from 'lucide-react';

interface PageCommentWidgetProps {
  pageUrl: string;
  pageTitle: string;
  isAdmin?: boolean;
}

const PageCommentWidget = ({ pageUrl, pageTitle, isAdmin = false }: PageCommentWidgetProps) => {
  const [isOpen, setIsOpen] = useState(false);
  const [comment, setComment] = useState('');
  const [category, setCategory] = useState<PageComment['category']>('general');
  const [priority, setPriority] = useState<PageComment['priority']>('medium');
  const [isSubmitting, setIsSubmitting] = useState(false);
  const [showSuccess, setShowSuccess] = useState(false);
  const [selectedElement, setSelectedElement] = useState<string>('');
  const [isElementSelectMode, setIsElementSelectMode] = useState(false);
  const widgetRef = useRef<HTMLDivElement>(null);

  // Handle element selection mode
  useEffect(() => {
    if (isElementSelectMode) {
      const handleClick = (e: MouseEvent) => {
        e.preventDefault();
        e.stopPropagation();

        const target = e.target as HTMLElement;
        if (target && target !== widgetRef.current && !widgetRef.current?.contains(target)) {
          const selector = generateSelector(target);
          setSelectedElement(selector);
          setIsElementSelectMode(false);

          // Remove highlight
          document.querySelectorAll('.comment-highlight').forEach(el => {
            el.classList.remove('comment-highlight');
          });
        }
      };

      document.addEventListener('click', handleClick);
      return () => document.removeEventListener('click', handleClick);
    }
  }, [isElementSelectMode]);

  const generateSelector = (element: HTMLElement): string => {
    if (element.id) {
      return `#${element.id}`;
    }
    if (element.className) {
      const classes = element.className.split(' ').filter(c => c).join('.');
      return `.${classes}`;
    }
    return element.tagName.toLowerCase();
  };

  const handleElementSelect = () => {
    setIsElementSelectMode(true);
    // Add highlight class to all clickable elements
    document.querySelectorAll('*').forEach(el => {
      if (el instanceof HTMLElement && el.offsetWidth > 0 && el.offsetHeight > 0) {
        el.classList.add('comment-highlight');
      }
    });
  };

  const handleSubmit = async () => {
    if (!comment.trim()) return;

    setIsSubmitting(true);
    try {
      await feedbackService.addComment({
        pageUrl,
        pageTitle,
        elementSelector: selectedElement,
        comment: comment.trim(),
        category,
        priority,
        status: 'open',
        createdBy: 'admin@fairfieldairportcars.com', // Gregg's email
        userAgent: navigator.userAgent
      });

      setComment('');
      setCategory('general');
      setPriority('medium');
      setSelectedElement('');
      setShowSuccess(true);
      setIsOpen(false);

      setTimeout(() => setShowSuccess(false), 3000);
    } catch (error) {
      console.error('Error submitting comment:', error);
    } finally {
      setIsSubmitting(false);
    }
  };

  const getCategoryIcon = (cat: string) => {
    switch (cat) {
      case 'bug': return <AlertCircle  />;
      case 'design': return <Star  />;
      case 'copy': return <MessageSquare  />;
      case 'feature': return <CheckCircle  />;
      default: return <MessageSquare  />;
    }
  };

  const getPriorityColor = (priority: string) => {
    switch (priority) {
      case 'urgent': return 'bg-red-100 text-red-800';
      case 'high': return 'bg-orange-100 text-orange-800';
      case 'medium': return 'bg-warning text-warning-dark';
      case 'low': return 'bg-green-100 text-green-800';
      default: return 'bg-gray-100 text-gray-800';
    }
  };

  if (!isAdmin) return null;

  return (
    <>
      {/* Floating Comment Button */}
      <Container>
        <Button
          onClick={() => setIsOpen(!isOpen)}
          aria-label="Add page comment"
        >
          <MessageSquare />
        </Button>
      </Container>

      {/* Comment Widget */}
      {isOpen && (
        <Container>
          <Container>
            <Card>
              <CardHeader>
                <CardTitle>Add Page Comment</CardTitle>
                <Button
                  variant="ghost"
                  size="sm"
                  onClick={() => setIsOpen(false)}
                >
                  <X />
                </Button>
              </CardHeader>
              <CardBody>
                <Container>
                  <Text size="sm">
                    Page: {pageTitle}
                  </Text>
                </Container>

                {/* Element Selection */}
                <Container>
                  <Text size="sm">
                    Element (Optional)
                  </Text>
                  <Container>
                    <Button
                      variant="outline"
                      size="sm"
                      onClick={handleElementSelect}
                      disabled={isElementSelectMode}
                    >
                      {selectedElement ? 'Change Element' : 'Select Element'}
                    </Button>
                    {selectedElement && (
                      <Button
                        variant="outline"
                        size="sm"
                        onClick={() => setSelectedElement('')}
                      >
                        Clear
                      </Button>
                    )}
                  </Container>
                  {selectedElement && (
                    <Text size="sm">
                      Selected: {selectedElement}
                    </Text>
                  )}
                  {isElementSelectMode && (
                    <Text size="sm">
                      Click on any element to select it
                    </Text>
                  )}
                </Container>

                {/* Category */}
                <Container>
                  <Text size="sm">
                    Category
                  </Text>
                  <Select value={category} onValueChange={(value: PageComment['category']) => setCategory(value)}>
                    <SelectTrigger>
                      <SelectValue />
                    </SelectTrigger>
                    <SelectContent>
                      <SelectItem value="general">
                        <Container>
                          <MessageSquare />
                          <Text>General</Text>
                        </Container>
                      </SelectItem>
                      <SelectItem value="bug">
                        <Container>
                          <AlertCircle />
                          <Text>Bug</Text>
                        </Container>
                      </SelectItem>
                      <SelectItem value="design">
                        <Container>
                          <Star />
                          <Text>Design</Text>
                        </Container>
                      </SelectItem>
                      <SelectItem value="copy">
                        <Container>
                          <MessageSquare />
                          <Text>Copy/Text</Text>
                        </Container>
                      </SelectItem>
                      <SelectItem value="feature">
                        <Container>
                          <CheckCircle />
                          <Text>Feature Request</Text>
                        </Container>
                      </SelectItem>
                    </SelectContent>
                  </Select>
                </Container>

                {/* Priority */}
                <Container>
                  <Text size="sm">
                    Priority
                  </Text>
                  <Select value={priority} onValueChange={(value: PageComment['priority']) => setPriority(value)}>
                    <SelectTrigger>
                      <SelectValue />
                    </SelectTrigger>
                    <SelectContent>
                      <SelectItem value="low">Low</SelectItem>
                      <SelectItem value="medium">Medium</SelectItem>
                      <SelectItem value="high">High</SelectItem>
                      <SelectItem value="urgent">Urgent</SelectItem>
                    </SelectContent>
                  </Select>
                </Container>

                {/* Comment */}
                <Container>
                  <Text size="sm">
                    Comment
                  </Text>
                  <Textarea
                    value={comment}
                    onChange={(e) => setComment(e.target.value)}
                    placeholder="Describe what you'd like to change or improve..."
                    rows={4}
                  />
                </Container>

                {/* Preview */}
                {comment && (
                  <Container>
                    <Container>
                      {getCategoryIcon(category)}
                      <Badge>
                        {priority}
                      </Badge>
                    </Container>
                    <Text>{comment}</Text>
                  </Container>
                )}

                {/* Submit Button */}
                <Container>
                  <Button
                    onClick={handleSubmit}
                    disabled={!comment.trim() || isSubmitting}
                  >
                    {isSubmitting ? 'Submitting...' : 'Submit Comment'}
                  </Button>
                  <Button
                    variant="outline"
                    onClick={() => setIsOpen(false)}
                    disabled={isSubmitting}
                  >
                    Cancel
                  </Button>
                </Container>
              </CardBody>
            </Card>
          </Container>
        </Container>
      )}

      {/* Success Message */}
      {showSuccess && (
        <div>
          <div>
            <CheckCircle />
            <span>Comment submitted successfully!</span>
          </div>
        </div>
      )}


      {/* Element Selection Styles */}
      <style jsx global>{`
        .comment-highlight {
          cursor: pointer !important;
          outline: 2px solid #3b82f6 !important;
          outline-offset: 2px !important;
          transition: outline 0.2s ease !important;
        }
        .comment-highlight:hover {
          outline: 2px solid #1d4ed8 !important;
          background-color: rgba(59, 130, 246, 0.1) !important;
        }
      `}</style>
    </>
  );
};

export default PageCommentWidget;

'use client';

import { useState, useEffect, useRef, ReactNode } from 'react';
import { X, CheckCircle, Clock } from 'lucide-react';
import { useAdmin } from './AdminProvider';
import { confluenceCommentsService, type ConfluenceComment } from '@/lib/business/confluence-comments';
import { Textarea, Select, Option, Container } from '@/components/ui';
import { Stack } from '@/components/ui/containers';

interface SimpleCommentSystemProps {
  children: ReactNode;
}

const SimpleCommentSystem = ({ children }: SimpleCommentSystemProps) => {
  const { isAdmin, commentMode } = useAdmin();
  const [comments, setComments] = useState<ConfluenceComment[]>([]);
  const [activeCommentBox, setActiveCommentBox] = useState<string | null>(null);
  const [commentText, setCommentText] = useState('');
  const [selectedElement, setSelectedElement] = useState<HTMLElement | null>(null);
  const commentBoxRef = useRef<HTMLDivElement>(null);

  // Load comments from Firebase
  useEffect(() => {
    if (isAdmin) {
      loadComments();
    }
  }, [isAdmin]);

  const loadComments = async () => {
    try {
      const commentsData = await confluenceCommentsService.getComments();
      setComments(commentsData);
    } catch (error) {
      console.error('Error loading comments:', error);
    }
  };

  // Handle left-click on any element (Confluence-style)
  useEffect(() => {
    if (!isAdmin || !commentMode) return;

    const handleClick = (e: MouseEvent) => {
      // Prevent default behavior when comment mode is active
      e.preventDefault();
      e.stopPropagation();

      const target = e.target as HTMLElement;
      if (!target) return;

      // Comprehensive exclusion of comment-related elements
      const isCommentElement = 
        target.closest('.comment-box') ||
        target.closest('.comment-icon') ||
        target.closest('.simple-comment-icon') ||
        target.closest('.comment-highlight') ||
        target.closest('.commentable-section') ||
        target.closest('[data-comment-id]') ||
        target.closest('.comment-mode-active') ||
        target.closest('[class*="comment"]') || // Any class containing "comment"
        target.closest('button') || // Exclude all buttons
        target.closest('a') || // Exclude all links
        target.closest('input') || // Exclude all inputs
        target.closest('textarea') || // Exclude all textareas
        target.closest('select') || // Exclude all selects
        target.closest('form'); // Exclude all forms

      if (isCommentElement) {
        console.log('üîí CommentSystem - Skipping comment element:', target.tagName, target.className);
        return;
      }

      // Generate unique ID for the element
      const elementId = generateElementId(target);
      const elementText = target.textContent?.trim() || target.tagName.toLowerCase();

      console.log('üí¨ CommentSystem - Adding comment to element:', elementText);

      setSelectedElement(target);
      setActiveCommentBox(elementId);
      setCommentText('');
    };

    document.addEventListener('click', handleClick);
    return () => document.removeEventListener('click', handleClick);
  }, [isAdmin, commentMode]);

  // Close comment box when clicking outside
  useEffect(() => {
    const handleClickOutside = (e: MouseEvent) => {
      if (commentBoxRef.current && !commentBoxRef.current.contains(e.target as Node)) {
        setActiveCommentBox(null);
        setSelectedElement(null);
      }
    };

    document.addEventListener('mousedown', handleClickOutside);
    return () => document.removeEventListener('mousedown', handleClickOutside);
  }, []);

  const generateElementId = (element: HTMLElement): string => {
    // Create a unique ID based on element properties
    const tagName = element.tagName.toLowerCase();
    const className = element.className || '';
    const textContent = element.textContent?.slice(0, 20) || '';
    const hash = btoa(`${tagName}-${className}-${textContent}`).replace(/[^a-zA-Z0-9]/g, '');
    return `comment-${hash}`;
  };

  const generateElementSelector = (element: HTMLElement): string => {
    // Generate a CSS selector for the element
    if (element.id) {
      return `#${element.id}`;
    }
    if (element.className && typeof element.className === 'string') {
      const classes = element.className.split(' ').filter(c => c).join('.');
      return `.${classes}`;
    }
    return element.tagName.toLowerCase();
  };

  const handleAddComment = async () => {
    if (!commentText.trim() || !selectedElement) return;

    const elementId = generateElementId(selectedElement);
    const elementText = selectedElement.textContent?.trim() || selectedElement.tagName.toLowerCase();
    const elementSelector = generateElementSelector(selectedElement);

    try {
      await confluenceCommentsService.addComment({
        elementId,
        elementText,
        elementSelector,
        pageUrl: window.location.pathname,
        pageTitle: document.title,
        comment: commentText.trim(),
        status: 'open',
        createdBy: 'admin@fairfieldairportcar.com'
      });

      await loadComments(); // Reload comments from Firebase
      setActiveCommentBox(null);
      setSelectedElement(null);
      setCommentText('');
    } catch (error) {
      console.error('Error adding comment:', error);
    }
  };

  const handleEditComment = async (commentId: string, newText: string) => {
    try {
      await confluenceCommentsService.updateComment(commentId, { comment: newText });
      await loadComments(); // Reload comments from Firebase
    } catch (error) {
      console.error('Error updating comment:', error);
    }
  };

  const handleStatusChange = async (commentId: string, newStatus: ConfluenceComment['status']) => {
    try {
      await confluenceCommentsService.updateComment(commentId, { status: newStatus });
      await loadComments(); // Reload comments from Firebase
    } catch (error) {
      console.error('Error updating comment status:', error);
    }
  };

  const handleDeleteComment = async (commentId: string) => {
    try {
      await confluenceCommentsService.deleteComment(commentId);
      await loadComments(); // Reload comments from Firebase
    } catch (error) {
      console.error('Error deleting comment:', error);
    }
  };

  const getExistingComments = (elementId: string) => {
    return comments.filter(comment => comment.elementId === elementId);
  };

  const existingComments = selectedElement ? getExistingComments(generateElementId(selectedElement)) : [];

  const getStatusIcon = (status: ConfluenceComment['status']) => {
    switch (status) {
      case 'open':
        return <Clock  />;
      case 'in-progress':
        return <Clock  />;
      case 'resolved':
        return <CheckCircle  />;
      default:
        return <Clock  />;
    }
  };

  const getStatusColor = (status: ConfluenceComment['status']) => {
    switch (status) {
      case 'open':
        return 'bg-orange-100 text-orange-800';
      case 'in-progress':
        return 'bg-blue-100 text-blue-800';
      case 'resolved':
        return 'bg-green-100 text-green-800';
      default:
        return 'bg-gray-100 text-gray-800';
    }
  };

  // Add comment icons to elements with comments
  useEffect(() => {
    if (!isAdmin) return;

    // Remove existing comment icons
    document.querySelectorAll('.simple-comment-icon').forEach(el => el.remove());

    // Add comment icons for elements with comments
    comments.forEach(comment => {
      // Try to find the element by various selectors
      let targetElement: HTMLElement | null = null;

      // First try to find by data attribute
      targetElement = document.querySelector(`[data-comment-id="${comment.elementId}"]`) as HTMLElement;

      // If not found, try to find by text content (approximate match)
      if (!targetElement) {
        const allElements = document.querySelectorAll('*');
        for (const element of allElements) {
          if (element instanceof HTMLElement && 
              element.textContent?.trim() === comment.elementText) {
            targetElement = element;
            break;
          }
        }
      }

      // If still not found, try to find by partial text match
      if (!targetElement) {
        const allElements = document.querySelectorAll('*');
        for (const element of allElements) {
          if (element instanceof HTMLElement && 
              element.textContent?.includes(comment.elementText.slice(0, 10))) {
            targetElement = element;
            break;
          }
        }
      }

      if (targetElement) {
        // Add data attribute for future reference
        targetElement.setAttribute('data-comment-id', comment.elementId);

        // Ensure the element has relative positioning
        if (getComputedStyle(targetElement).position === 'static') {
          targetElement.style.position = 'relative';
        }

        const icon = document.createElement('div');
        icon.className = 'simple-comment-icon absolute top-1 right-1 z-50 cursor-pointer';

        // Determine icon color based on comment status
        const commentStatuses = comments.filter(c => c.elementId === comment.elementId).map(c => c.status);
        const hasOpen = commentStatuses.includes('open');
        const hasInProgress = commentStatuses.includes('in-progress');
        const hasResolved = commentStatuses.includes('resolved');

        let iconColor = 'bg-blue-500 hover:bg-blue-600';
        if (hasOpen) {
          iconColor = 'bg-red-500 hover:bg-red-600';
        } else if (hasInProgress) {
          iconColor = 'bg-yellow-500 hover:bg-yellow-600';
        } else if (hasResolved) {
          iconColor = 'bg-green-500 hover:bg-green-600';
        }

        icon.innerHTML = `
          <div class="${iconColor} text-white rounded-full w-5 h-5 flex items-center justify-center text-xs shadow-lg transition-colors">
            üí¨
          </div>
        `;
        icon.onclick = (e) => {
          e.stopPropagation();
          setSelectedElement(targetElement);
          setActiveCommentBox(comment.elementId);
          setCommentText('');
        };

        // Remove any existing icon for this element
        targetElement.querySelectorAll('.simple-comment-icon').forEach(el => el.remove());
        targetElement.appendChild(icon);
      }
    });
  }, [comments, isAdmin]);

  if (!isAdmin) {
    return <>{children}</>;
  }

  return (
    <>
      {children}

      {/* Comment Box */}
      {activeCommentBox && selectedElement && (
        <Container
          ref={commentBoxRef}
          style={{
            top: selectedElement.getBoundingClientRect().top - 10,
            left: selectedElement.getBoundingClientRect().right + 10,
          }}
        >
          <Stack direction="horizontal" align="center" justify="between">
            <h4>
              {existingComments.length > 0 ? `Comments (${existingComments.length})` : 'Add Comment'}
            </h4>
            <button
              onClick={() => {
                setActiveCommentBox(null);
                setSelectedElement(null);
              }}
            >
              <X />
            </button>
          </Stack>

          <Container>
            <strong>Element:</strong> {selectedElement.textContent?.slice(0, 50) || selectedElement.tagName.toLowerCase()}
          </Container>

          {/* Existing Comments */}
          {existingComments.length > 0 && (
            <Stack spacing="md">
              {existingComments.map(comment => (
                <Container key={comment.id}>
                  <Stack direction="horizontal" align="center" justify="between">
                    <Container>
                      {getStatusIcon(comment.status)}
                      <span>
                        {comment.status}
                      </span>
                    </Container>
                    <button
                      onClick={() => handleDeleteComment(comment.id)}
                    >
                      <X />
                    </button>
                  </Stack>

                  <Textarea
                    value={comment.comment}
                    onChange={(e) => handleEditComment(comment.id, e.target.value)}
                    rows={2}
                  />

                  <Container>
                    <Select
                      value={comment.status}
                      onChange={(e: React.ChangeEvent<HTMLSelectElement>) => handleStatusChange(comment.id, e.target.value as ConfluenceComment['status'])}
                    >
                      <Option value="open">Open</Option>
                      <Option value="in-progress">In Progress</Option>
                      <Option value="resolved">Resolved</Option>
                    </Select>
                  </Container>
                </Container>
              ))}
            </Stack>
          )}

          {/* New Comment Input */}
          <Container>
            <Textarea
              value={commentText}
              onChange={(e) => setCommentText(e.target.value)}
              placeholder="Add a new comment..."
              rows={3}
              autoFocus
            />

            <Stack direction="horizontal" spacing="sm">
              <button
                onClick={handleAddComment}
                disabled={!commentText.trim()}
              >
                Add Comment
              </button>
              <button
                onClick={() => {
                  setActiveCommentBox(null);
                  setSelectedElement(null);
                }}
              >
                Close
              </button>
            </Stack>
          </Container>
        </Container>
      )}

      {/* Global Styles */}
      <style jsx global>{`
        .simple-comment-icon {
          transition: all 0.2s ease;
        }

        .simple-comment-icon:hover {
          transform: scale(1.1);
        }

        .comment-mode-active * {
          cursor: pointer !important;
        }

        .comment-mode-active *:hover {
          outline: 2px dashed #3b82f6 !important;
          outline-offset: 1px !important;
          background-color: rgba(59, 130, 246, 0.05) !important;
        }

        .comment-mode-active *:hover::after {
          content: "üí¨";
          position: absolute;
          top: 2px;
          right: 2px;
          background: #3b82f6;
          color: white;
          border-radius: 50%;
          width: 16px;
          height: 16px;
          display: flex;
          align-items: center;
          justify-content: center;
          font-size: 8px;
          z-index: 50;
        }
      `}</style>
    </>
  );
};

export default SimpleCommentSystem;

import React from 'react';
import { Calendar, Clock, MapPin, DollarSign } from 'lucide-react';
import { Booking } from '@/types/booking';
import { Badge } from '@/components/ui';
import { Button } from '@/components/ui/button';
import { cn } from '@/lib/utils/utils';
import { Container, H3, Text, Span } from '@/components/ui';
import { Stack } from '@/components/ui/containers';

interface BookingCardProps {
  booking: Booking;
  className?: string;
  showActions?: boolean;
  onAction?: (action: 'edit' | 'cancel' | 'view') => void;
}

const BookingCard: React.FC<BookingCardProps> = ({
  booking,
  className,
  showActions = false,
  onAction
}) => {
  const getStatusColor = (status: Booking['status']) => {
    switch (status) {
      case 'confirmed':
        return 'bg-success text-text-inverse';
      case 'pending':
        return 'bg-warning text-text-inverse';
      case 'completed':
        return 'bg-info text-text-inverse';
      case 'cancelled':
        return 'bg-error text-text-inverse';
      default:
        return 'bg-gray-100';
    }
  };

  const formatDate = (dateString: string) => {
    return new Date(dateString).toLocaleDateString('en-US', {
      weekday: 'short',
      month: 'short',
      day: 'numeric'
    });
  };

  const formatTime = (dateString: string) => {
    return new Date(dateString).toLocaleTimeString('en-US', {
      hour: 'numeric',
      minute: '2-digit',
      hour12: true
    });
  };

  return (
    <Container className={className}>
      {/* Header */}
      <Stack direction="horizontal" align="center" justify="between" spacing="md">
        <Container>
          <H3>
            {booking.name}
          </H3>
          <Text>
            Booking #{booking.id}
          </Text>
        </Container>
        <Badge>
          {booking.status.charAt(0).toUpperCase() + booking.status.slice(1)}
        </Badge>
      </Stack>

      {/* Date and Time */}
      <Stack direction="horizontal" spacing="md">
        <Container>
          <Calendar />
          <Text size="sm">{formatDate(booking.pickupDateTime.toString())}</Text>
        </Container>
        <Container>
          <Clock />
          <Text size="sm">{formatTime(booking.pickupDateTime.toString())}</Text>
        </Container>
      </Stack>

      {/* Locations */}
      <Stack spacing="md">
        <Container>
          <MapPin />
          <Container>
            <Text size="xs">Pickup:</Text>
            <Text size="sm">{booking.pickupLocation}</Text>
          </Container>
        </Container>
        <Container>
          <MapPin />
          <Container>
            <Text size="xs">Drop-off:</Text>
            <Text size="sm">{booking.dropoffLocation}</Text>
          </Container>
        </Container>
      </Stack>

      {/* Passenger Info */}
      <Container>
        <Text size="sm">
          Passengers: {booking.passengers}
        </Text>
        {booking.notes && (
          <Container>
            <Text size="xs">Notes:</Text>
            <Text size="sm">{booking.notes}</Text>
          </Container>
        )}
      </Container>

      {/* Price */}
      <Stack direction="horizontal" align="center" spacing="sm">
        <DollarSign />
        <Text size="sm">Total Fare:</Text>
        <Text size="lg">
          ${booking.fare}
        </Text>
      </Stack>

      {/* Actions */}
      {showActions && onAction && (
        <Stack direction="horizontal" spacing="sm">
          <Button
            variant="ghost"
            size="sm"
            onClick={() => onAction('view')}
          >
            View Details
          </Button>
          {booking.status === 'pending' && (
            <>
              <Button
                variant="ghost"
                size="sm"
                onClick={() => onAction('edit')}
              >
                Edit
              </Button>
              <Button
                variant="ghost"
                size="sm"
                onClick={() => onAction('cancel')}
              >
                Cancel
              </Button>
            </>
          )}
        </Stack>
      )}
    </Container>
  );
};

export { BookingCard };

import React, { useState, useEffect, useRef } from 'react';
import { MapPin, Loader2 } from 'lucide-react';
import { Label } from '@/components/ui/label';
import { Button } from '@/components/ui/button';
import { cn } from '@/lib/utils/utils';
import { Container, Text, Span } from '@/components/ui';

interface LocationAutocompleteProps {
  value: string;
  onChange: (value: string) => void;
  onSelect?: (place: any) => void;
  placeholder?: string;
  label?: string;
  required?: boolean;
  error?: string;
  helperText?: string;
  className?: string;
  fieldId?: string;
}

const LocationAutocomplete: React.FC<LocationAutocompleteProps> = ({
  value,
  onChange,
  onSelect,
  placeholder = 'Enter location...',
  label,
  required = false,
  error,
  helperText,
  className,
  fieldId
}) => {
  const [suggestions, setSuggestions] = useState<any[]>([]);
  const [isLoading, setIsLoading] = useState(false);
  const [showSuggestions, setShowSuggestions] = useState(false);
  const inputRef = useRef<HTMLInputElement>(null);
  const suggestionsRef = useRef<HTMLDivElement>(null);

  useEffect(() => {
    const handleClickOutside = (event: MouseEvent) => {
      if (
        suggestionsRef.current &&
        !suggestionsRef.current.contains(event.target as Node) &&
        inputRef.current &&
        !inputRef.current.contains(event.target as Node)
      ) {
        setShowSuggestions(false);
      }
    };

    document.addEventListener('mousedown', handleClickOutside);
    return () => document.removeEventListener('mousedown', handleClickOutside);
  }, []);

  const searchPlaces = async (query: string) => {
    if (!query.trim()) {
      setSuggestions([]);
      return;
    }

    setIsLoading(true);
    try {
      const response = await fetch(`/api/places-autocomplete?query=${encodeURIComponent(query)}`);
      if (response.ok) {
        const data = await response.json();
        setSuggestions(data.predictions || []);
      }
    } catch (error) {
      console.error('Error fetching places:', error);
    } finally {
      setIsLoading(false);
    }
  };

  const handleInputChange = (e: React.ChangeEvent<HTMLInputElement>) => {
    const newValue = e.target.value;
    onChange(newValue);

    if (newValue.length > 2) {
      searchPlaces(newValue);
      setShowSuggestions(true);
    } else {
      setSuggestions([]);
      setShowSuggestions(false);
    }
  };

  const handleSuggestionClick = (suggestion: any) => {
    onChange(suggestion.description);
    if (onSelect) {
      onSelect(suggestion);
    }
    setShowSuggestions(false);
    setSuggestions([]);
  };

  const handleInputFocus = () => {
    if (suggestions.length > 0) {
      setShowSuggestions(true);
    }
  };

  return (
    <Container className={className}>
      {label && (
        <Label htmlFor={fieldId}>
          {label}
          {required && <Span>*</Span>}
        </Label>
      )}

      <Container>
        <div>
          {isLoading && (
            <Loader2 />
          )}
          <input
            ref={inputRef}
            id={fieldId}
            type="text"
            value={value}
            onChange={handleInputChange}
            onFocus={handleInputFocus}
            placeholder={placeholder}
          />
          {!isLoading && (
            <MapPin />
          )}
        </div>

        {error && (
          <Text size="sm">{error}</Text>
        )}

        {helperText && !error && (
          <Text size="sm">{helperText}</Text>
        )}
      </Container>

      {showSuggestions && suggestions.length > 0 && (
        <div ref={suggestionsRef}>
          {suggestions.map((suggestion, index) => (
            <Button
              key={index}
              variant="ghost"
              onClick={() => handleSuggestionClick(suggestion)}
            >
              <div>
                <MapPin />
                <div>
                  <div>
                    {suggestion.structured_formatting?.main_text || suggestion.description}
                  </div>
                  {suggestion.structured_formatting?.secondary_text && (
                    <Text size="sm">
                      {suggestion.structured_formatting.secondary_text}
                    </Text>
                  )}
                </div>
              </div>
            </Button>
          ))}
        </div>
      )}
    </Container>
  );
};

export { LocationAutocomplete };

import * as React from 'react';
import { cn } from '@/lib/utils/utils';
import { ErrorBoundary } from '@/components/ui/ErrorBoundary';
import {
  Table,
  TableBody,
  TableCell,
  TableHead,
  TableHeader,
  TableRow,
} from '@/components/ui/table';
import { Container, Text, H3 } from '@/components/ui';
import { Stack } from '@/components/ui/containers';

/**
 * Column configuration for the data table
 */
interface Column<T> {
  /** Unique key for the column */
  key: string;
  /** Display label for the column header */
  label: string;
  /** Custom render function for the column content */
  render?: (item: T) => React.ReactNode;
  /** Whether the column is sortable */
  sortable?: boolean;
  /** CSS width for the column */
  width?: string;
  /** Whether the column should be hidden on mobile */
  hideOnMobile?: boolean;
}

/**
 * A flexible data table component with sorting, loading states, and error handling
 * 
 * @example
 * ```tsx
 * // Basic usage
 * <DataTable
 *   data={users}
 *   columns={[
 *     { key: 'name', label: 'Name' },
 *     { key: 'email', label: 'Email' },
 *     { key: 'role', label: 'Role' }
 *   ]}
 * />
 * 
 * // With custom rendering
 * <DataTable
 *   data={bookings}
 *   columns={[
 *     { key: 'name', label: 'Name' },
 *     { 
 *       key: 'status', 
 *       label: 'Status',
 *       render: (booking) => <StatusBadge status={booking.status} />
 *     },
 *     { key: 'amount', label: 'Amount' }
 *   ]}
 *   onRowClick={(booking) => navigate(`/booking/${booking.id}`)}
 * />
 * 
 * // With loading state
 * <DataTable
 *   data={data}
 *   columns={columns}
 *   loading={isLoading}
 *   emptyMessage="No bookings found"
 * />
 * ```
 */
interface DataTableProps<T> {
  /** Array of data items to display */
  data: T[];
  /** Column configuration */
  columns: Column<T>[];
  /** Callback when a row is clicked */
  onRowClick?: (item: T) => void;
  /** Whether the table is in a loading state */
  loading?: boolean;
  /** Custom message to display when no data is available */
  emptyMessage?: React.ReactNode;
  /** Additional CSS classes */
  className?: string;
  /** Custom error boundary fallback */
  errorFallback?: React.ComponentType<{ error?: Error; resetError: () => void }>;
  /** Whether to show row hover effects */
  hoverable?: boolean;
  /** Custom loading component */
  loadingComponent?: React.ReactNode;
}

/**
 * Default loading skeleton component
 */
const LoadingSkeleton: React.FC<{ className?: string }> = ({ className }) => (
      <Container>
      <Stack spacing="md">
        <Container>
          <span>Loading...</span>
        </Container>
        {[...Array(5)].map((_, i) => (
          <Container key={i}>
            <span>Loading row {i + 1}...</span>
          </Container>
        ))}
      </Stack>
    </Container>
);

/**
 * Default empty state component
 */
const EmptyState: React.FC<{ message: React.ReactNode; className?: string }> = ({ 
  message, 
  className 
}) => (
  <Container>
    <Text>{message}</Text>
  </Container>
);

function DataTableComponent<T extends { id?: string }>({
  data,
  columns,
  onRowClick,
  loading = false,
  emptyMessage = 'No data available',
  className,
  hoverable = true,
  loadingComponent,
}: DataTableProps<T>) {
  // Memoize the filtered columns to prevent unnecessary re-renders
  const visibleColumns = React.useMemo(() => {
    return columns.filter(column => !column.hideOnMobile || window.innerWidth >= 768);
  }, [columns]);

  if (loading) {
    return loadingComponent || <LoadingSkeleton className={className} />;
  }

  if (data.length === 0) {
    return <EmptyState message={emptyMessage} className={className} />;
  }

  return (
    <Container className={className}>
      <Table>
        <TableHeader>
          <TableRow>
            {visibleColumns.map((column) => (
              <TableHead
                key={column.key}
                style={{ width: column.width }}
              >
                {column.label}
              </TableHead>
            ))}
          </TableRow>
        </TableHeader>
        <TableBody>
          {data.map((item, index) => (
            <TableRow
              key={item.id || index}
              onClick={() => onRowClick?.(item)}
              role={onRowClick ? 'button' : undefined}
              tabIndex={onRowClick ? 0 : undefined}
              onKeyDown={(e) => {
                if (onRowClick && (e.key === 'Enter' || e.key === ' ')) {
                  e.preventDefault();
                  onRowClick(item);
                }
              }}
            >
              {visibleColumns.map((column) => (
                <TableCell 
                  key={column.key}
                >
                  {column.render
                    ? column.render(item)
                    : String((item as Record<string, unknown>)[column.key] || '')}
                </TableCell>
              ))}
            </TableRow>
          ))}
        </TableBody>
      </Table>
    </Container>
  );
}

// Memoize the component for better performance
const DataTable = React.memo(DataTableComponent) as typeof DataTableComponent;

// Wrap with error boundary
const DataTableWithErrorBoundary = <T extends { id?: string }>(
  props: DataTableProps<T>
) => (
  <ErrorBoundary fallback={props.errorFallback}>
    <DataTable {...props} />
  </ErrorBoundary>
);

export { DataTableWithErrorBoundary as DataTable };

