import * as React from 'react';
import { cn } from '@/lib/utils/utils';
import { Button } from '@/components/ui/button';
import { Container, Span } from '@/components/ui';
import { Stack } from '@/components/ui/containers';

interface StarRatingProps extends React.HTMLAttributes<HTMLDivElement> {
  rating: number;
  maxRating?: number;
  size?: 'sm' | 'md' | 'lg';
  interactive?: boolean;
  onRatingChange?: (rating: number) => void;
  showValue?: boolean;
}

const StarRating = React.forwardRef<HTMLDivElement, StarRatingProps>(
  ({ 
    className, 
    rating, 
    maxRating = 5, 
    size = 'md',
    interactive = false,
    onRatingChange,
    showValue = false,
    ...props 
  }, ref) => {
    const [hoverRating, setHoverRating] = React.useState(0);

    const sizeClasses = {
      sm: 'w-4 h-4',
      md: 'w-6 h-6',
      lg: 'w-8 h-8',
    };

    const handleStarClick = (starValue: number) => {
      if (interactive && onRatingChange) {
        onRatingChange(starValue);
      }
    };

    const handleStarHover = (starValue: number) => {
      if (interactive) {
        setHoverRating(starValue);
      }
    };

    const handleMouseLeave = () => {
      if (interactive) {
        setHoverRating(0);
      }
    };

    return (
      <Stack direction="horizontal" align="center" spacing="sm" className={className} onMouseLeave={handleMouseLeave} {...props}>
        {[...Array(maxRating)].map((_, index) => {
          const starValue = index + 1;
          const isFilled = starValue <= (hoverRating || rating);

          return (
            <button
              key={starValue}
              type="button"
              onClick={() => handleStarClick(starValue)}
              onMouseEnter={() => handleStarHover(starValue)}
              disabled={!interactive}
            >
              <svg
                fill="currentColor"
                viewBox="0 0 20 20"
              >
                <path d="M9.049 2.927c.3-.921 1.603-.921 1.902 0l1.286 3.957a1 1 0 00.95.69h4.162c.969 0 1.371 1.24.588 1.81l-3.368 2.448a1 1 0 00-.364 1.118l1.287 3.957c.3.921-.755 1.688-1.54 1.118l-3.368-2.448a1 1 0 00-1.175 0l-3.368 2.448c-.784.57-1.838-.197-1.539-1.118l1.287-3.957a1 1 0 00-.364-1.118L2.24 9.384c-.783-.57-.38-1.81.588-1.81h4.162a1 1 0 00.95-.69L9.049 2.927z" />
              </svg>
            </button>
          );
        })}
        {showValue && (
          <Span>
            {rating}/{maxRating}
          </Span>
        )}
      </Stack>
    );
  }
);
StarRating.displayName = 'StarRating';

export { StarRating };

import * as React from 'react';
import { cn } from '@/lib/utils/utils';
import { Label } from '@/components/ui/label';
import { Input } from '@/components/ui';
import { ErrorBoundary } from '@/components/ui/ErrorBoundary';
import { Container, Span } from '@/components/ui';

/**
 * A form field component that combines label, input, validation, and error handling
 * with enhanced accessibility and performance optimizations
 * 
 * @example
 * ```tsx
 * // Basic usage
 * <FormField
 *   label="Email"
 *   type="email"
 *   placeholder="Enter your email"
 * />
 * 
 * // With validation
 * <FormField
 *   label="Password"
 *   type="password"
 *   required
 *   error="Password must be at least 8 characters"
 *   helperText="Use a strong password"
 * />
 * 
 * // With custom validation
 * <FormField
 *   label="Age"
 *   type="number"
 *   min={18}
 *   max={100}
 *   error={ageError}
 * />
 * ```
 */
interface FormFieldProps extends React.InputHTMLAttributes<HTMLInputElement> {
  /** The label text for the field */
  label: string;
  /** Error message to display below the field */
  error?: string;
  /** Helper text to display below the field */
  helperText?: string;
  /** Whether the field is required */
  required?: boolean;
  /** Custom error boundary fallback */
  errorFallback?: React.ComponentType<{ error?: Error; resetError: () => void }>;
}

const FormFieldComponent = React.forwardRef<HTMLInputElement, FormFieldProps>(
  ({ 
    className, 
    label, 
    error, 
    helperText, 
    required = false, 
    id, 
    errorFallback,
    type,
    ...props 
  }, ref) => {
    const fieldId = id || `field-${label.toLowerCase().replace(/\s+/g, '-')}`;

    return (
      <ErrorBoundary fallback={errorFallback}>
        <Container >
          <Label htmlFor={fieldId} >
            {label}
            {required && <Span >*</Span>}
          </Label>
          <Input
            id={fieldId}
            className={cn(
              error && 'border-error focus-visible:ring-error',
              className
            )}
            required={required}
            type={type as any}
            placeholder={props.placeholder}
            value={props.value as string}
            onChange={props.onChange}
            onBlur={props.onBlur}
            onFocus={props.onFocus}
            disabled={props.disabled}
            name={props.name}
          />
        </Container>
      </ErrorBoundary>
    );
  }
);
FormFieldComponent.displayName = 'FormField';

// Memoize the component for better performance
const FormField = React.memo(FormFieldComponent);

export { FormField };

