          <Container maxWidth={containerMaxWidth}>
            <PageHeader align={headerAlign} padding="none" margin="none">
              {homeContent.hero?.title && (
                <H1>
                  {homeContent.hero.title}
                </H1>
              )}
              {homeContent.hero?.subtitle && (
                <H2 >
                  {homeContent.hero.subtitle}
                </H2>
              )}
              {homeContent.hero?.ctaText && (
                <Lead>
                  {homeContent.hero.ctaText}
                </Lead>
              )}
            </PageHeader>
          </Container>
        </Section>
      )}

      {/* Main Content */}
      <Section padding="xl">
        <Container maxWidth={containerMaxWidth}>
          <PageContent padding="none" margin="none">
            {children}
          </PageContent>
        </Container>
      </Section>
    </CMSLayout>
  );
};

