import { Label, Text } from '@/components/ui';
import { cn } from '@/lib/utils/utils';

// Enhanced Input Component
interface EnhancedInputProps extends Omit<React.InputHTMLAttributes<HTMLInputElement>, 'size'> {
  label?: string;
  error?: string;
  helper?: string;
  variant?: 'default' | 'filled' | 'outlined';
  size?: 'sm' | 'md' | 'lg';
  leftIcon?: React.ReactNode;
  rightIcon?: React.ReactNode;
}

const EnhancedInput = React.forwardRef<HTMLInputElement, EnhancedInputProps>(
  ({ className, label, error, helper, variant = 'default', size = 'md', leftIcon, rightIcon, ...props }, ref) => {
    const variantClasses = {
      default: 'border-border-primary focus:border-brand-primary',
      filled: 'bg-bg-secondary border-border-primary focus:border-brand-primary',
      outlined: 'border-2 border-border-primary focus:border-brand-primary'
    };

    const sizeClasses = {
      sm: 'text-sm p-3',
      md: 'text-base p-4',
      lg: 'text-lg p-4'
    };

    const inputClasses = cn(
      'w-full transition-colors duration-200',
      variantClasses[variant],
      sizeClasses[size],
      error && 'border-error focus:border-error',
      leftIcon && 'pl-10',
      rightIcon && 'pr-10',
      className
    );

    return (
      <div >
        {label && (
          <Label>
            {label}
          </Label>
        )}
        <div >
          {leftIcon && (
            <div className="absolute left-3 top-1/2 transform -translate-y-1/2">
              {leftIcon}
            </div>
          )}
          <Input
            className={inputClasses}
            {...(props as any)}
          />
          {rightIcon && (
            <div className="absolute right-3 top-1/2 transform -translate-y-1/2">
              {rightIcon}
            </div>
          )}
        </div>
        {error && (
          <Text size="sm">{error}</Text>
        )}
        {helper && !error && (
          <Text size="sm">{helper}</Text>
        )}
      </div>
    );
  }
);
EnhancedInput.displayName = 'EnhancedInput';

// Enhanced Textarea Component
interface EnhancedTextareaProps extends React.TextareaHTMLAttributes<HTMLTextAreaElement> {
  label?: string;
  error?: string;
  helper?: string;
  variant?: 'default' | 'filled' | 'outlined';
  size?: 'sm' | 'md' | 'lg';
}

const EnhancedTextarea = React.forwardRef<HTMLTextAreaElement, EnhancedTextareaProps>(
  ({ className, label, error, helper, variant = 'default', size = 'md', ...props }, ref) => {
    const variantClasses = {
      default: 'border-border-primary focus:border-brand-primary',
      filled: 'bg-bg-secondary border-border-primary focus:border-brand-primary',
      outlined: 'border-2 border-border-primary focus:border-brand-primary'
    };

    const sizeClasses = {
      sm: 'text-sm p-3',
      md: 'text-base p-4',
      lg: 'text-lg p-4'
    };

    const textareaClasses = cn(
      'w-full transition-colors duration-200',
      variantClasses[variant],
      sizeClasses[size],
      error && 'border-error focus:border-error',
      className
    );

    return (
      <div >
        {label && (
          <Label>
            {label}
          </Label>
        )}
        <Textarea
          className={textareaClasses}
          {...(props as any)}
        />
        {error && (
          <Text size="sm">{error}</Text>
        )}
        {helper && !error && (
          <Text size="sm">{helper}</Text>
        )}
      </div>
    );
  }
);
EnhancedTextarea.displayName = 'EnhancedTextarea';

// Enhanced Select Component
interface EnhancedSelectProps {
  label?: string;
  error?: string;
  helper?: string;
  placeholder?: string;
  value?: string;
  onValueChange?: (value: string) => void;
  children: React.ReactNode;
  variant?: 'default' | 'filled' | 'outlined';
  size?: 'sm' | 'md' | 'lg';
  className?: string;
}

const EnhancedSelect: React.FC<EnhancedSelectProps> = ({
  label,
  error,
  helper,
  placeholder,
  value,
  onValueChange,
  children,
  variant = 'default',
  size = 'md',
  className
}) => {
  const variantClasses = {
    default: 'border-border-primary focus:border-brand-primary',
    filled: 'bg-bg-secondary border-border-primary focus:border-brand-primary',
    outlined: 'border-2 border-border-primary focus:border-brand-primary'
  };

  const sizeClasses = {
    sm: 'text-sm p-3',
    md: 'text-base p-4',
    lg: 'text-lg p-4'
  };

  const selectClasses = cn(
    'w-full transition-colors duration-200',
    variantClasses[variant],
    sizeClasses[size],
    error && 'border-error focus:border-error',
    className
  );

  return (
