export interface EnhancedBooking {
  id?: string;
  name: string;
  email: string;
  phone: string;
  pickupLocation: string;
  dropoffLocation: string;
  pickupDateTime: Date;
  passengers: number;
  status: 'pending' | 'confirmed' | 'in-progress' | 'completed' | 'cancelled';
  baseFare: number;
  dynamicFare: number;
  surgeMultiplier: number;
  depositPaid: boolean;
  balanceDue: number;
  flightNumber?: string;
  notes?: string;
  driverId?: string;
  driverName?: string;
  estimatedArrival?: Date;
  actualArrival?: Date;
  tipAmount?: number;
  cancellationFee?: number;
  squareOrderId?: string;
  depositAmount?: number;
  reminderSent?: boolean;
  onMyWaySent?: boolean;
  createdAt: Date;
  updatedAt: Date;
  // Enhanced fields
  distance: number; // in miles
  duration: number; // in minutes
  trafficConditions: 'low' | 'medium' | 'high';
  weatherConditions: 'clear' | 'rain' | 'snow' | 'storm';
  airportRushHour: boolean;
  specialRequests: string[];
  customerRating?: number;
  driverRating?: number;
  feedback?: string;
}

export interface PricingConfig {
  baseRate: number; // per mile
  airportFee: number;
  lateNightFee: number;
  earlyMorningFee: number;
  passengerFee: number; // per additional passenger
  surgePricing: {
    enabled: boolean;
    maxMultiplier: number;
    factors: {
      airportRushHour: number;
      weather: number;
      traffic: number;
      specialEvents: number;
    };
  };
  cancellation: {
    over24hRefundPercent: number;
    between3And24hRefundPercent: number;
    under3hRefundPercent: number;
  };
}

export interface Driver {
  id: string;
  name: string;
  phone: string;
  email: string;
  status: 'available' | 'busy' | 'offline';
  currentLocation?: {
    lat: number;
    lng: number;
  };
  rating: number;
  totalRides: number;
  vehicleInfo: {
    make: string;
    model: string;
    year: number;
    color: string;
    licensePlate: string;
  };
  availability: {
    startTime: string; // HH:MM
    endTime: string; // HH:MM
    daysOfWeek: number[]; // 0-6, Sunday = 0
  };
  lastUpdated: Date;
}

class EnhancedBookingService {
  private pricingConfig: PricingConfig = {
    baseRate: 2.50, // $2.50 per mile
    airportFee: 15.00,
    lateNightFee: 10.00,
    earlyMorningFee: 8.00,
    passengerFee: 5.00,
    surgePricing: {
      enabled: true,
      maxMultiplier: 2.5,
      factors: {
        airportRushHour: 1.3,
        weather: 1.2,
        traffic: 1.15,
        specialEvents: 1.4,
      },
    },
    cancellation: {
      over24hRefundPercent: 100,
      between3And24hRefundPercent: 50,
      under3hRefundPercent: 0,
    },
  };

  // Calculate dynamic fare with surge pricing
  async calculateDynamicFare(
    pickupLocation: string,
    dropoffLocation: string,
    pickupDateTime: Date,
    passengers: number = 1
  ): Promise<{
    baseFare: number;
    dynamicFare: number;
    surgeMultiplier: number;
    breakdown: {
      distance: number;
      baseRate: number;
      airportFee: number;
      timeFee: number;
      passengerFee: number;
      surgeFee: number;
    };
  }> {
    // Get distance and duration from Google Maps API
    const routeInfo = await this.getRouteInfo(pickupLocation, dropoffLocation);

    // Calculate base fare
    const baseFare = routeInfo.distance * this.pricingConfig.baseRate;

    // Calculate additional fees
    const airportFee = this.isAirportLocation(dropoffLocation) ? this.pricingConfig.airportFee : 0;
    const timeFee = this.calculateTimeFee(pickupDateTime);
    const passengerFee = Math.max(0, passengers - 1) * this.pricingConfig.passengerFee;

    // Calculate surge pricing
    const surgeMultiplier = await this.calculateSurgeMultiplier(pickupDateTime, routeInfo);
    const surgeFee = (baseFare + airportFee + timeFee + passengerFee) * (surgeMultiplier - 1);

    const dynamicFare = baseFare + airportFee + timeFee + passengerFee + surgeFee;

    return {
      baseFare,
      dynamicFare,
      surgeMultiplier,
      breakdown: {
        distance: routeInfo.distance,
        baseRate: baseFare,
        airportFee,
        timeFee,
        passengerFee,
        surgeFee,
      },
    };
  }

  // Get route information from Google Maps API
  private async getRouteInfo(pickup: string, dropoff: string): Promise<{ distance: number; duration: number }> {
    try {
      const response = await fetch('/api/route-info', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({ pickup, dropoff }),
      });

      if (!response.ok) {
        throw new Error('Failed to get route information');
      }

      const data = await response.json();
      return {
        distance: data.distance, // in miles
        duration: data.duration, // in minutes
      };
    } catch (error) {
      console.error('Error getting route info:', error);
      // Fallback to estimated distance
      return {
        distance: 25, // Default estimate
        duration: 30, // Default estimate
      };
    }
  }

  // Check if location is an airport
  private isAirportLocation(location: string): boolean {
    const airports = [
      'JFK', 'LGA', 'EWR', 'BDL', 'HPN', 'ISP', 'SWF',
      'John F. Kennedy', 'LaGuardia', 'Newark', 'Bradley',
      'Westchester', 'Islip', 'Stewart'
    ];

    return airports.some(airport => 
      location.toUpperCase().includes(airport.toUpperCase())
    );
  }

  // Calculate time-based fees
  private calculateTimeFee(pickupDateTime: Date): number {
    const hour = pickupDateTime.getHours();

    // Late night fee (10 PM - 6 AM)
    if (hour >= 22 || hour < 6) {
      return this.pricingConfig.lateNightFee;
    }

    // Early morning fee (6 AM - 9 AM)
    if (hour >= 6 && hour < 9) {
      return this.pricingConfig.earlyMorningFee;
    }

    return 0;
  }

  // Calculate surge pricing multiplier
  private async calculateSurgeMultiplier(
    pickupDateTime: Date,
    routeInfo: { distance: number; duration: number }
  ): Promise<number> {
    if (!this.pricingConfig.surgePricing.enabled) {
      return 1.0;
    }

    let multiplier = 1.0;
    const factors = this.pricingConfig.surgePricing.factors;

    // Check for airport rush hour
    if (this.isAirportRushHour(pickupDateTime)) {
      multiplier *= factors.airportRushHour;
    }

    // Check weather conditions (would need weather API integration)
    const weatherMultiplier = await this.getWeatherMultiplier(pickupDateTime);
    multiplier *= weatherMultiplier;

    // Check traffic conditions
    const trafficMultiplier = this.getTrafficMultiplier(routeInfo.duration, routeInfo.distance);
    multiplier *= trafficMultiplier;

    // Check for special events
    const eventMultiplier = await this.getSpecialEventMultiplier(pickupDateTime);
    multiplier *= eventMultiplier;

    // Cap at maximum multiplier
    return Math.min(multiplier, this.pricingConfig.surgePricing.maxMultiplier);
  }

  // Check if it's airport rush hour
  private isAirportRushHour(dateTime: Date): boolean {
    const hour = dateTime.getHours();
    const dayOfWeek = dateTime.getDay();

    // Weekday rush hours
    if (dayOfWeek >= 1 && dayOfWeek <= 5) {
      // Morning rush (6 AM - 10 AM)
      if (hour >= 6 && hour <= 10) return true;
      // Evening rush (4 PM - 8 PM)
      if (hour >= 16 && hour <= 20) return true;
    }

    // Weekend airport traffic (Friday evening, Sunday evening)
    if (dayOfWeek === 5 && hour >= 16) return true; // Friday evening
    if (dayOfWeek === 0 && hour >= 16) return true; // Sunday evening

    return false;
  }

  // Get weather multiplier (placeholder for weather API integration)
  private async getWeatherMultiplier(dateTime: Date): Promise<number> {
    // This would integrate with a weather API
    // For now, return base multiplier
    return 1.0;
  }

  // Get traffic multiplier based on travel time vs distance
  private getTrafficMultiplier(duration: number, distance: number): number {
    const expectedSpeed = 30; // mph
    const actualSpeed = distance / (duration / 60);

    if (actualSpeed < expectedSpeed * 0.7) {
      return this.pricingConfig.surgePricing.factors.traffic;
    }

    return 1.0;
  }

  // Get special event multiplier (placeholder for event API integration)
  private async getSpecialEventMultiplier(dateTime: Date): Promise<number> {
    // This would integrate with an events API
    // For now, return base multiplier
    return 1.0;
  }

  // Create enhanced booking
  async createEnhancedBooking(bookingData: Omit<EnhancedBooking, 'id' | 'createdAt' | 'updatedAt'>): Promise<string> {
    // Calculate dynamic fare
    const fareInfo = await this.calculateDynamicFare(
      bookingData.pickupLocation,
      bookingData.dropoffLocation,
      bookingData.pickupDateTime,
      bookingData.passengers
    );

    // Get route info for enhanced data
    const routeInfo = await this.getRouteInfo(
      bookingData.pickupLocation,
      bookingData.dropoffLocation
    );

    const enhancedBooking: Omit<EnhancedBooking, 'id'> = {
      ...bookingData,
      baseFare: fareInfo.baseFare,
      dynamicFare: fareInfo.dynamicFare,
      surgeMultiplier: fareInfo.surgeMultiplier,
      balanceDue: fareInfo.dynamicFare - (bookingData.depositPaid ? bookingData.depositAmount || 0 : 0),
      distance: routeInfo.distance,
      duration: routeInfo.duration,
      trafficConditions: this.getTrafficConditions(routeInfo.duration, routeInfo.distance),
      weatherConditions: 'clear', // Would be set by weather API
      airportRushHour: this.isAirportRushHour(bookingData.pickupDateTime),
      specialRequests: bookingData.specialRequests || [],
      createdAt: new Date(),
      updatedAt: new Date(),
    };

    const docRef = await addDoc(collection(db, 'bookings'), enhancedBooking);
    return docRef.id;
  }

  // Get traffic conditions
  private getTrafficConditions(duration: number, distance: number): 'low' | 'medium' | 'high' {
    const expectedSpeed = 30; // mph
    const actualSpeed = distance / (duration / 60);

    if (actualSpeed < expectedSpeed * 0.7) return 'high';
    if (actualSpeed < expectedSpeed * 0.9) return 'medium';
    return 'low';
  }

  // Find available drivers
  async findAvailableDrivers(pickupDateTime: Date, location: string): Promise<Driver[]> {
    const driversRef = collection(db, 'drivers');
    const q = query(
      driversRef,
      where('status', '==', 'available'),
      orderBy('rating', 'desc'),
      limit(5)
    );

    const snapshot = await getDocs(q);
    const drivers: Driver[] = [];

    snapshot.forEach((doc) => {
      const driverData = doc.data() as Driver;
      driverData.id = doc.id;

      // Check if driver is available at the requested time
      if (this.isDriverAvailable(driverData, pickupDateTime)) {
        drivers.push(driverData);
      }
    });

    return drivers;
  }

  // Check if driver is available at specific time
  private isDriverAvailable(driver: Driver, pickupDateTime: Date): boolean {
    const hour = pickupDateTime.getHours();
    const dayOfWeek = pickupDateTime.getDay();

    // Check if driver works on this day
    if (!driver.availability.daysOfWeek.includes(dayOfWeek)) {
      return false;
    }

    // Check if pickup time is within driver's hours
    const [startHour] = driver.availability.startTime.split(':').map(Number);
    const [endHour] = driver.availability.endTime.split(':').map(Number);

    return hour >= startHour && hour <= endHour;
  }

  // Update booking status with enhanced tracking
  async updateBookingStatus(
    bookingId: string, 
    status: EnhancedBooking['status'], 
    driverId?: string
  ): Promise<void> {
    const bookingRef = doc(db, 'bookings', bookingId);

    const updateData: any = {
      status,
      updatedAt: new Date(),
    };

    if (driverId) {
      updateData.driverId = driverId;
      // Get driver info
      const driverDoc = await getDoc(doc(db, 'drivers', driverId));
      if (driverDoc.exists()) {
        const driverData = driverDoc.data() as Driver;
        updateData.driverName = driverData.name;
      }
    }

    await updateDoc(bookingRef, updateData);
  }

  // Get booking analytics
  async getBookingAnalytics(): Promise<{
    totalBookings: number;
    totalRevenue: number;
    averageFare: number;
    surgePricingRevenue: number;
    popularRoutes: Array<{ route: string; count: number }>;
    peakHours: Array<{ hour: number; bookings: number }>;
  }> {
    const bookingsRef = collection(db, 'bookings');
    const snapshot = await getDocs(bookingsRef);

    const bookings: EnhancedBooking[] = [];
    snapshot.forEach((doc) => {
      bookings.push({ id: doc.id, ...doc.data() } as EnhancedBooking);
    });

    const totalBookings = bookings.length;
    const totalRevenue = bookings.reduce((sum, booking) => sum + booking.dynamicFare, 0);
    const averageFare = totalBookings > 0 ? totalRevenue / totalBookings : 0;
    const surgePricingRevenue = bookings.reduce((sum, booking) => 
      sum + (booking.dynamicFare - booking.baseFare), 0
    );

    // Calculate popular routes
    const routeCounts: Record<string, number> = {};
    bookings.forEach(booking => {
      const route = `${booking.pickupLocation} ‚Üí ${booking.dropoffLocation}`;
      routeCounts[route] = (routeCounts[route] || 0) + 1;
    });

    const popularRoutes = Object.entries(routeCounts)
      .map(([route, count]) => ({ route, count }))
      .sort((a, b) => b.count - a.count)
      .slice(0, 5);

    // Calculate peak hours
    const hourCounts: Record<number, number> = {};
    bookings.forEach(booking => {
      const hour = new Date(booking.pickupDateTime).getHours();
      hourCounts[hour] = (hourCounts[hour] || 0) + 1;
    });

    const peakHours = Object.entries(hourCounts)
      .map(([hour, bookings]) => ({ hour: parseInt(hour), bookings }))
      .sort((a, b) => b.bookings - a.bookings)
      .slice(0, 5);

    return {
      totalBookings,
      totalRevenue,
      averageFare,
      surgePricingRevenue,
      popularRoutes,
      peakHours,
    };
  }
}

// Export singleton instance
export const enhancedBookingService = new EnhancedBookingService();

// Performance Optimization Service
// Handles code splitting, bundle optimization, and performance monitoring

import React from 'react';

interface PerformanceMetrics {
  pageLoadTime: number;
  timeToInteractive: number;
  bundleSize: number;
  apiResponseTime: number;
  errorRate: number;
}

interface OptimizationConfig {
  enableCodeSplitting: boolean;
  enableImageOptimization: boolean;
  enableCaching: boolean;
  enableCompression: boolean;
  performanceBudget: {
    pageLoadTime: number; // ms
    bundleSize: number; // bytes
    apiResponseTime: number; // ms
  };
}

class PerformanceOptimizer {
  private metrics: PerformanceMetrics[] = [];
  private config: OptimizationConfig;

  constructor(config: OptimizationConfig) {
    this.config = config;
    this.setupPerformanceMonitoring();
  }

  // Monitor Core Web Vitals
  private setupPerformanceMonitoring() {
    if (typeof window === 'undefined') return;

    // Monitor Largest Contentful Paint (LCP)
    const observer = new PerformanceObserver((list) => {
      const entries = list.getEntries();
      const lastEntry = entries[entries.length - 1];
      if (lastEntry) {
        this.recordMetric('lcp', lastEntry.startTime);
      }
    });
    observer.observe({ entryTypes: ['largest-contentful-paint'] });

    // Monitor First Input Delay (FID)
    const fidObserver = new PerformanceObserver((list) => {
      const entries = list.getEntries();
      entries.forEach((entry) => {
        const firstInputEntry = entry as any;
        if (firstInputEntry.processingStart) {
          this.recordMetric('fid', firstInputEntry.processingStart - firstInputEntry.startTime);
        }
      });
    });
    fidObserver.observe({ entryTypes: ['first-input'] });

    // Monitor Cumulative Layout Shift (CLS)
    let clsValue = 0;
    const clsObserver = new PerformanceObserver((list) => {
      const entries = list.getEntries();
      entries.forEach((entry: any) => {
        if (!entry.hadRecentInput) {
          clsValue += entry.value;
        }
      });
      this.recordMetric('cls', clsValue);
    });
    clsObserver.observe({ entryTypes: ['layout-shift'] });
  }

  // Record performance metric
  private recordMetric(type: string, value: number) {
    const metric = {
      type,
      value,
      timestamp: Date.now(),
      url: window.location.href
    };

    // Store metric
    this.metrics.push({
      pageLoadTime: type === 'pageLoad' ? value : 0,
      timeToInteractive: type === 'tti' ? value : 0,
      bundleSize: type === 'bundleSize' ? value : 0,
      apiResponseTime: type === 'apiResponse' ? value : 0,
      errorRate: type === 'errorRate' ? value : 0
    });

    // Send to analytics in production
    if (process.env.NODE_ENV === 'production') {
      this.sendToAnalytics(metric);
    }

    // Log in development
    if (process.env.NODE_ENV === 'development') {
      console.log(`üìä Performance Metric: ${type} = ${value}ms`);
    }
  }

  // Optimize images
  optimizeImage(src: string, width: number, height: number): string {
    if (!this.config.enableImageOptimization) return src;

    // Use Next.js Image optimization
    return `/_next/image?url=${encodeURIComponent(src)}&w=${width}&q=75`;
  }

  // Lazy load components
  lazyLoadComponent<T extends React.ComponentType<any>>(
    importFunc: () => Promise<{ default: T }>,
    fallback?: React.ReactNode
  ): React.LazyExoticComponent<T> {
    return React.lazy(() => 
      importFunc().then(module => ({
        default: module.default
      }))
    );
  }

  // Optimize API calls with caching
  async cachedApiCall<T>(
    url: string,
    options: RequestInit = {},
    cacheTime: number = 5 * 60 * 1000 // 5 minutes
  ): Promise<T> {
    if (!this.config.enableCaching) {
      return this.makeApiCall<T>(url, options);
    }

    const cacheKey = `api-cache-${url}-${JSON.stringify(options)}`;
    const cached = sessionStorage.getItem(cacheKey);

    if (cached) {
      const { data, timestamp } = JSON.parse(cached);
      if (Date.now() - timestamp < cacheTime) {
        return data;
      }
    }

    const data = await this.makeApiCall<T>(url, options);

    // Cache the result
    sessionStorage.setItem(cacheKey, JSON.stringify({
      data,
      timestamp: Date.now()
    }));

    return data;
  }

  private async makeApiCall<T>(url: string, options: RequestInit = {}): Promise<T> {
    const startTime = Date.now();

    try {
      const response = await fetch(url, {
        ...options,
        headers: {
          'Content-Type': 'application/json',
          ...options.headers
        }
      });

      const responseTime = Date.now() - startTime;
      this.recordMetric('apiResponse', responseTime);

      if (!response.ok) {
        throw new Error(`API call failed: ${response.status}`);
      }

      return await response.json();
    } catch (error) {
      this.recordMetric('errorRate', 1);
      throw error;
    }
  }

  // Bundle size optimization
  optimizeBundleSize(): void {
    if (!this.config.enableCodeSplitting) return;

    // Dynamic imports for heavy components
    const heavyComponents = {
      DataTable: () => import('@/components/ui/DataTable'),
      // Only include components that actually exist
    };

    // Preload critical components
    Object.entries(heavyComponents).forEach(([name, importFunc]) => {
      if (document.querySelector(`[data-component="${name}"]`)) {
        importFunc();
      }
    });
  }

  // Compression optimization
  enableCompression(): void {
    if (!this.config.enableCompression) return;

    // Enable gzip compression for text assets
    const textAssets = document.querySelectorAll('link[rel="stylesheet"], script[type="text/javascript"]');
    textAssets.forEach((asset) => {
      if (asset instanceof HTMLLinkElement) {
        asset.href = asset.href.replace(/\.(css|js)$/, '.gz.$1');
      }
    });
  }

  // Performance budget checking
  checkPerformanceBudget(metrics: PerformanceMetrics): boolean {
    const budget = this.config.performanceBudget;

    const violations = [
      metrics.pageLoadTime > budget.pageLoadTime && 'Page load time exceeded budget',
      metrics.bundleSize > budget.bundleSize && 'Bundle size exceeded budget',
      metrics.apiResponseTime > budget.apiResponseTime && 'API response time exceeded budget'
    ].filter(Boolean);

    if (violations.length > 0) {
      console.warn('üö® Performance budget violations:', violations);
      return false;
    }

    return true;
  }

  // Get performance report
  getPerformanceReport(): {
    averagePageLoadTime: number;
    averageApiResponseTime: number;
    errorRate: number;
    recommendations: string[];
  } {
    if (this.metrics.length === 0) {
      return {
        averagePageLoadTime: 0,
        averageApiResponseTime: 0,
        errorRate: 0,
        recommendations: ['No performance data available']
      };
    }

    const avgPageLoadTime = this.metrics.reduce((sum, m) => sum + m.pageLoadTime, 0) / this.metrics.length;
    const avgApiResponseTime = this.metrics.reduce((sum, m) => sum + m.apiResponseTime, 0) / this.metrics.length;
    const errorRate = this.metrics.reduce((sum, m) => sum + m.errorRate, 0) / this.metrics.length;

    const recommendations: string[] = [];

    if (avgPageLoadTime > 3000) {
      recommendations.push('Consider implementing code splitting for faster page loads');
    }

    if (avgApiResponseTime > 1000) {
      recommendations.push('Optimize API endpoints or implement caching');
    }

    if (errorRate > 0.05) {
      recommendations.push('Investigate and fix error sources');
    }

    return {
      averagePageLoadTime: avgPageLoadTime,
      averageApiResponseTime: avgApiResponseTime,
      errorRate,
      recommendations
    };
  }

  private async sendToAnalytics(metric: any) {
    try {
      await fetch('/api/analytics/performance', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify(metric)
      });
    } catch (error) {
      console.error('Failed to send performance metric:', error);
    }
  }
}

// Default configuration
const defaultConfig: OptimizationConfig = {
  enableCodeSplitting: true,
  enableImageOptimization: true,
  enableCaching: true,
  enableCompression: true,
  performanceBudget: {
    pageLoadTime: 3000, // 3 seconds
    bundleSize: 500 * 1024, // 500KB
    apiResponseTime: 1000 // 1 second
  }
};

// Export singleton instance
export const performanceOptimizer = new PerformanceOptimizer(defaultConfig);

// Export types
export type { PerformanceMetrics, OptimizationConfig };

// User Experience Service
// Handles real-time tracking, offline capabilities, accessibility, and mobile optimization

import { performanceOptimizer } from './performance-optimizer';

interface UserPreferences {
  accessibility: {
    highContrast: boolean;
    reduceMotion: boolean;
    fontSize: 'normal' | 'large' | 'extra-large';
    screenReader: boolean;
  };
  notifications: {
    email: boolean;
    sms: boolean;
    push: boolean;
    reminderTime: number; // hours before pickup
  };
  language: string;
  timezone: string;
}

interface TrackingData {
  bookingId: string;
  driverLocation?: {
    lat: number;
    lng: number;
    timestamp: Date;
    heading: number;
    speed: number;
  };
  estimatedArrival?: Date;
  status: 'confirmed' | 'driver-assigned' | 'en-route' | 'arrived' | 'completed';
  lastUpdated: Date;
}

interface OfflineData {
  bookings: any[];
  userPreferences: UserPreferences;
  cachedRoutes: Record<string, any>;
  pendingActions: Array<{
    type: 'booking' | 'payment' | 'feedback';
    data: any;
    timestamp: Date;
  }>;
}

class UserExperienceService {
  private userPreferences: UserPreferences;
  private trackingData: Map<string, TrackingData> = new Map();
  private offlineData: OfflineData;
  private websocketConnection?: WebSocket;
  private isOnline: boolean = navigator.onLine;

  constructor() {
    this.userPreferences = this.loadUserPreferences();
    this.offlineData = this.loadOfflineData();
    this.setupEventListeners();
    this.initializeAccessibility();
  }

  // Load user preferences from localStorage
  private loadUserPreferences(): UserPreferences {
    if (typeof window === 'undefined') {
      return this.getDefaultPreferences();
    }

    const stored = localStorage.getItem('user-preferences');
    if (stored) {
      try {
        return { ...this.getDefaultPreferences(), ...JSON.parse(stored) };
      } catch (error) {
        console.error('Failed to load user preferences:', error);
      }
    }

    return this.getDefaultPreferences();
  }

  // Get default user preferences
  private getDefaultPreferences(): UserPreferences {
    return {
      accessibility: {
        highContrast: false,
        reduceMotion: false,
        fontSize: 'normal',
        screenReader: false,
      },
      notifications: {
        email: true,
        sms: true,
        push: false,
        reminderTime: 24,
      },
      language: 'en',
      timezone: Intl.DateTimeFormat().resolvedOptions().timeZone,
    };
  }

  // Load offline data from localStorage
  private loadOfflineData(): OfflineData {
    if (typeof window === 'undefined') {
      return this.getDefaultOfflineData();
    }

    const stored = localStorage.getItem('offline-data');
    if (stored) {
      try {
        return { ...this.getDefaultOfflineData(), ...JSON.parse(stored) };
      } catch (error) {
        console.error('Failed to load offline data:', error);
      }
    }

    return this.getDefaultOfflineData();
  }

  // Get default offline data
  private getDefaultOfflineData(): OfflineData {
    return {
      bookings: [],
      userPreferences: this.getDefaultPreferences(),
      cachedRoutes: {},
      pendingActions: [],
    };
  }

  // Setup event listeners for online/offline status
  private setupEventListeners(): void {
    if (typeof window === 'undefined') return;

    // Online/offline status
    window.addEventListener('online', () => {
      this.isOnline = true;
      this.syncOfflineData();
      this.showNotification('Connection restored', 'You are back online');
    });

    window.addEventListener('offline', () => {
      this.isOnline = false;
      this.showNotification('Connection lost', 'You are offline. Some features may be limited.');
    });

    // Before unload - save current state
    window.addEventListener('beforeunload', () => {
      this.saveOfflineData();
    });

    // Service worker for offline functionality
    if ('serviceWorker' in navigator) {
      navigator.serviceWorker.register('/sw.js').catch(console.error);
    }
  }

  // Initialize accessibility features
  private initializeAccessibility(): void {
    if (typeof window === 'undefined') return;

    // Apply accessibility settings
    this.applyAccessibilitySettings();

    // Setup keyboard navigation
    this.setupKeyboardNavigation();

    // Setup screen reader announcements
    this.setupScreenReaderAnnouncements();
  }

  // Apply accessibility settings to the page
  private applyAccessibilitySettings(): void {
    const root = document.documentElement;
    const settings = this.userPreferences.accessibility;

    // High contrast
    root.classList.toggle('high-contrast', settings.highContrast);

    // Reduced motion
    root.classList.toggle('reduce-motion', settings.reduceMotion);

    // Font size
    root.classList.remove('font-large', 'font-extra-large');
    if (settings.fontSize === 'large') {
      root.classList.add('font-large');
    } else if (settings.fontSize === 'extra-large') {
      root.classList.add('font-extra-large');
    }

    // Screen reader support
    if (settings.screenReader) {
      this.enhanceScreenReaderSupport();
    }
  }

  // Setup keyboard navigation
  private setupKeyboardNavigation(): void {
    document.addEventListener('keydown', (event) => {
      // Skip to main content
      if (event.key === 'Tab' && event.altKey) {
        event.preventDefault();
        const mainContent = document.querySelector('main');
        if (mainContent) {
          (mainContent as HTMLElement).focus();
        }
      }

      // Escape key to close modals
      if (event.key === 'Escape') {
        const modals = document.querySelectorAll('[role="dialog"]');
        modals.forEach(modal => {
          if (modal.classList.contains('open')) {
            this.closeModal(modal as HTMLElement);
          }
        });
      }
    });
  }

  // Setup screen reader announcements
  private setupScreenReaderAnnouncements(): void {
    // Create live region for announcements
    const announcementRegion = document.createElement('div');
    announcementRegion.setAttribute('aria-live', 'polite');
    announcementRegion.setAttribute('aria-atomic', 'true');
    announcementRegion.style.position = 'absolute';
    announcementRegion.style.left = '-10000px';
    announcementRegion.style.width = '1px';
    announcementRegion.style.height = '1px';
    announcementRegion.style.overflow = 'hidden';
    announcementRegion.id = 'screen-reader-announcements';

    document.body.appendChild(announcementRegion);
  }

  // Enhance screen reader support
  private enhanceScreenReaderSupport(): void {
    // Add ARIA labels to interactive elements
    const buttons = document.querySelectorAll('button:not([aria-label])');
    buttons.forEach(button => {
      const text = button.textContent?.trim();
      if (text) {
        button.setAttribute('aria-label', text);
      }
    });

    // Add skip links
    this.addSkipLinks();
  }

  // Add skip links for keyboard navigation
  private addSkipLinks(): void {
    const skipLinks = [
      { href: '#main-content', text: 'Skip to main content' },
      { href: '#navigation', text: 'Skip to navigation' },
      { href: '#booking-form', text: 'Skip to booking form' },
    ];

    const skipLinksContainer = document.createElement('div');
    skipLinksContainer.className = 'skip-links';

    skipLinks.forEach(link => {
      const skipLink = document.createElement('a');
      skipLink.href = link.href;
      skipLink.textContent = link.text;
      skipLink.className = 'skip-link';
      skipLinksContainer.appendChild(skipLink);
    });

    document.body.insertBefore(skipLinksContainer, document.body.firstChild);
  }

  // Close modal
  private closeModal(modal: HTMLElement): void {
    modal.classList.remove('open');
    modal.setAttribute('aria-hidden', 'true');

    // Return focus to trigger element
    const trigger = document.querySelector(`[data-modal="${modal.id}"]`);
    if (trigger) {
      (trigger as HTMLElement).focus();
    }
  }

  // Start real-time tracking for a booking
  startTracking(bookingId: string): void {
    if (!this.isOnline) {
      console.warn('Cannot start tracking while offline');
      return;
    }

    // Initialize tracking data
    this.trackingData.set(bookingId, {
      bookingId,
      status: 'confirmed',
      lastUpdated: new Date(),
    });

    // Connect to WebSocket for real-time updates
    this.connectWebSocket(bookingId);

    // Start periodic location updates
    this.startLocationUpdates(bookingId);
  }

  // Connect to WebSocket for real-time updates
  private connectWebSocket(bookingId: string): void {
    const wsUrl = `${window.location.protocol === 'https:' ? 'wss:' : 'ws:'}//${window.location.host}/api/ws/tracking/${bookingId}`;

    this.websocketConnection = new WebSocket(wsUrl);

    this.websocketConnection.onopen = () => {
      console.log('WebSocket connected for tracking');
    };

    this.websocketConnection.onmessage = (event) => {
      const data = JSON.parse(event.data);
      this.updateTrackingData(bookingId, data);
    };

    this.websocketConnection.onerror = (error) => {
      console.error('WebSocket error:', error);
      // Fallback to polling
      this.startPolling(bookingId);
    };

    this.websocketConnection.onclose = () => {
      console.log('WebSocket disconnected');
      // Fallback to polling
      this.startPolling(bookingId);
    };
  }

  // Start polling as fallback for WebSocket
  private startPolling(bookingId: string): void {
    const pollInterval = setInterval(async () => {
      try {
        const response = await fetch(`/api/booking/${bookingId}/status`);
        const data = await response.json();
        this.updateTrackingData(bookingId, data);
      } catch (error) {
        console.error('Polling error:', error);
        clearInterval(pollInterval);
      }
    }, 10000); // Poll every 10 seconds
  }

  // Start location updates for driver tracking
  private startLocationUpdates(bookingId: string): void {
    // This would integrate with driver's GPS
    // For now, simulate location updates
    const locationInterval = setInterval(() => {
      // Simulate driver location updates
      const mockLocation = {
        lat: 40.7128 + (Math.random() - 0.5) * 0.01,
        lng: -74.0060 + (Math.random() - 0.5) * 0.01,
        timestamp: new Date(),
        heading: Math.random() * 360,
        speed: 30 + Math.random() * 20,
      };

      this.updateTrackingData(bookingId, { driverLocation: mockLocation });
    }, 30000); // Update every 30 seconds
  }

  // Update tracking data
  private updateTrackingData(bookingId: string, data: Partial<TrackingData>): void {
    const currentData = this.trackingData.get(bookingId);
    if (!currentData) return;

    const updatedData = { ...currentData, ...data, lastUpdated: new Date() };
    this.trackingData.set(bookingId, updatedData);

    // Update UI
    this.updateTrackingUI(bookingId, updatedData);

    // Announce to screen reader
    this.announceToScreenReader(this.getTrackingAnnouncement(updatedData));
  }

  // Update tracking UI
  private updateTrackingUI(bookingId: string, data: TrackingData): void {
    // Update status display
    const statusElement = document.querySelector(`[data-booking-id="${bookingId}"] .status`);
    if (statusElement) {
      statusElement.textContent = data.status;
      statusElement.className = `status status-${data.status}`;
    }

    // Update map if available
    if (data.driverLocation) {
      this.updateMap(bookingId, data.driverLocation);
    }

    // Update estimated arrival
    if (data.estimatedArrival) {
      this.updateEstimatedArrival(bookingId, data.estimatedArrival);
    }
  }

  // Update map with driver location
  private updateMap(bookingId: string, location: TrackingData['driverLocation']): void {
    // This would integrate with Google Maps API
    // For now, just log the location
    console.log(`Driver location for booking ${bookingId}:`, location);
  }

  // Update estimated arrival time
  private updateEstimatedArrival(bookingId: string, estimatedArrival: Date): void {
    const etaElement = document.querySelector(`[data-booking-id="${bookingId}"] .eta`);
    if (etaElement) {
      const minutes = Math.round((estimatedArrival.getTime() - Date.now()) / 60000);
      etaElement.textContent = `${minutes} minutes`;
    }
  }

  // Get tracking announcement for screen reader
  private getTrackingAnnouncement(data: TrackingData): string {
    if (data.status === 'en-route' && data.driverLocation) {
      return 'Driver is on the way to your location';
    } else if (data.status === 'arrived') {
      return 'Driver has arrived at your location';
    } else if (data.status === 'completed') {
      return 'Your ride has been completed';
    }
    return '';
  }

  // Announce to screen reader
  private announceToScreenReader(message: string): void {
    if (!message) return;

    const announcementRegion = document.getElementById('screen-reader-announcements');
    if (announcementRegion) {
      announcementRegion.textContent = message;
      setTimeout(() => {
        announcementRegion.textContent = '';
      }, 1000);
    }
  }

  // Show notification to user
  showNotification(title: string, message: string, type: 'info' | 'success' | 'warning' | 'error' = 'info'): void {
    // Check if browser supports notifications
    if ('Notification' in window && Notification.permission === 'granted') {
      new Notification(title, { body: message, icon: '/favicon.ico' });
    }

    // Show in-app notification
    this.showInAppNotification(title, message, type);
  }

  // Show in-app notification
  private showInAppNotification(title: string, message: string, type: string): void {
    const notification = document.createElement('div');
    notification.className = `notification notification-${type}`;
    notification.innerHTML = `
      <div class="notification-header">
        <h4>${title}</h4>
        <button class="notification-close" aria-label="Close notification">√ó</button>
      </div>
      <p>${message}</p>
    `;

    // Add to notification container
    const container = document.getElementById('notification-container') || this.createNotificationContainer();
    container.appendChild(notification);

    // Auto-remove after 5 seconds
    setTimeout(() => {
      notification.remove();
    }, 5000);

    // Close button functionality
    const closeButton = notification.querySelector('.notification-close');
    if (closeButton) {
      closeButton.addEventListener('click', () => notification.remove());
    }
  }

  // Create notification container
  private createNotificationContainer(): HTMLElement {
    const container = document.createElement('div');
    container.id = 'notification-container';
    container.className = 'notification-container';
    document.body.appendChild(container);
    return container;
  }

  // Save offline data
  private saveOfflineData(): void {
    if (typeof window === 'undefined') return;

    try {
      localStorage.setItem('offline-data', JSON.stringify(this.offlineData));
    } catch (error) {
      console.error('Failed to save offline data:', error);
    }
  }

  // Sync offline data when back online
  private async syncOfflineData(): Promise<void> {
    if (!this.isOnline) return;

    // Process pending actions
    for (const action of this.offlineData.pendingActions) {
      try {
        await this.processPendingAction(action);
      } catch (error) {
        console.error('Failed to process pending action:', error);
      }
    }

    // Clear pending actions
    this.offlineData.pendingActions = [];
    this.saveOfflineData();
  }

  // Process pending action
  private async processPendingAction(action: OfflineData['pendingActions'][0]): Promise<void> {
    switch (action.type) {
      case 'booking':
        await fetch('/api/booking', {
          method: 'POST',
          headers: { 'Content-Type': 'application/json' },
          body: JSON.stringify(action.data),
        });
        break;
      case 'payment':
        await fetch('/api/payment/process', {
          method: 'POST',
          headers: { 'Content-Type': 'application/json' },
          body: JSON.stringify(action.data),
        });
        break;
      case 'feedback':
        await fetch('/api/feedback', {
          method: 'POST',
          headers: { 'Content-Type': 'application/json' },
          body: JSON.stringify(action.data),
        });
        break;
    }
  }

  // Add pending action for offline processing
  addPendingAction(type: OfflineData['pendingActions'][0]['type'], data: any): void {
    this.offlineData.pendingActions.push({
      type,
      data,
      timestamp: new Date(),
    });
    this.saveOfflineData();
  }

  // Update user preferences
  updateUserPreferences(preferences: Partial<UserPreferences>): void {
    this.userPreferences = { ...this.userPreferences, ...preferences };
    this.applyAccessibilitySettings();

    // Save to localStorage
    if (typeof window !== 'undefined') {
      localStorage.setItem('user-preferences', JSON.stringify(this.userPreferences));
    }
  }

  // Get current user preferences
  getUserPreferences(): UserPreferences {
    return { ...this.userPreferences };
  }

  // Check if user is online
  isUserOnline(): boolean {
    return this.isOnline;
  }

  // Get tracking data for a booking
  getTrackingData(bookingId: string): TrackingData | undefined {
    return this.trackingData.get(bookingId);
  }

  // Stop tracking for a booking
  stopTracking(bookingId: string): void {
    this.trackingData.delete(bookingId);

    if (this.websocketConnection) {
      this.websocketConnection.close();
    }
  }
}

// Export singleton instance
export const userExperienceService = new UserExperienceService();

// Export types
export type { UserPreferences, TrackingData, OfflineData };
